import React, { useState, useEffect } from 'react';
import { useOutletContext } from 'react-router-dom';
import API from '../api';

export default function CustomerHistoryPage() {
  const { dataSource } = useOutletContext();
  const [customerHistory, setCustomerHistory] = useState([]);
  const [startDate, setStartDate] = useState('');
  const [endDate, setEndDate] = useState('');
  const [error, setError] = useState('');
  const [loading, setLoading] = useState(false);

  useEffect(() => {
    fetchCustomerHistory();
  }, [dataSource]);

  // --- REFACTORED: This function is now simpler and more efficient ---
  const fetchCustomerHistory = async (from, to) => {
    try {
      setError('');
      setLoading(true);
      const token = localStorage.getItem('token');
      const headers = { 'Authorization': `Bearer ${token}` };

      if (dataSource === 'shopify') {
        // Step 1: Ensure the backend data is up-to-date with Shopify
        await API.get('/shopify/sync/customers', { headers });
        await API.get('/shopify/sync/orders', { headers });
      }

      // Step 2: Fetch the final, pre-processed data from our own backend
      const params = new URLSearchParams();
      if (from) params.append('from', from);
      if (to) params.append('to', to);
      const res = await API.get(`/dashboard/customer-history?${params.toString()}`, { headers });
      
      setCustomerHistory(res.data.customerHistory || []);

    } catch (err) {
      console.error('API fetch error:', err.response || err);
      setError('Failed to fetch customer history.');
      setCustomerHistory([]);
    } finally {
      setLoading(false);
    }
  };

  const handleFilterHistory = () => {
    fetchCustomerHistory(startDate, endDate);
  };

  return (
    <div className="widget">
      <h3>Customer History</h3>
      <div style={{ display: 'flex', gap: '10px', marginBottom: '20px' }}>
        <label>From:</label>
        <input type="date" value={startDate} onChange={e => setStartDate(e.target.value)} />
        <label>To:</label>
        <input type="date" value={endDate} onChange={e => setEndDate(e.target.value)} />
        <button onClick={handleFilterHistory}>Filter</button>
      </div>

      {error && <div style={{ color: 'red', marginBottom: '10px' }}>{error}</div>}
      {loading && <div>Loading customer history...</div>}

      <table border="1" cellPadding="8" style={{ borderCollapse: 'collapse', width: '100%' }}>
        <thead>
          <tr>
            <th>Customer Name</th>
            <th>Total Spent</th>
            <th>Last Order Date</th>
            {/* --- ADDED: New column for events --- */}
            <th>Recent Activity</th>
          </tr>
        </thead>
        <tbody>
          {customerHistory.length === 0 ? (
            <tr>
              <td colSpan="4" style={{ textAlign: 'center' }}>No customer history found</td>
            </tr>
          ) : (
            customerHistory.map(customer => (
              <tr key={customer.id}>
                <td>{customer.firstName} {customer.lastName}</td>
                <td>₹{parseFloat(customer.totalSpent || 0).toFixed(2)}</td>
                <td>{customer.lastOrderDate ? new Date(customer.lastOrderDate).toLocaleDateString() : 'N/A'}</td>
                {/* --- ADDED: New cell to display events --- */}
                <td>
                  <ul style={{ margin: 0, paddingLeft: '15px', listStyleType: 'disc' }}>
                    {(customer.Events || []).map((event, index) => (
                      <li key={index}>
                        {event.eventName} on {new Date(event.createdAt).toLocaleDateString()}
                      </li>
                    ))}
                  </ul>
                </td>
              </tr>
            ))
          )}
        </tbody>
      </table>
    </div>
  );
}
