import React, { useState } from 'react';
import API from '../api';
export default function Login({ onLogin }){
  const [name, setName] = useState('');
  const [tenantId, setTenantId] = useState('');
  const [storeUrl, setStoreUrl] = useState('');
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');

  const signup = async ()=>{
    const res = await API.post('/auth/signup', { name, storeUrl, email, password });
    onLogin(res.data.token);
  };
  const quickLogin = async ()=>{
    // quick tenant id login removed in favor of email/password demo - kept for convenience
    if(tenantId){
      const res = await API.post('/auth/login', { tenantId });
      onLogin(res.data.token);
      return;
    }
    const res = await API.post('/auth/login', { email, password });
    onLogin(res.data.token);
  };
  return (
    <div style={{maxWidth:420}}>
      <h3>Signup (create tenant & admin)</h3>
      <input placeholder='Store name' value={name} onChange={e=>setName(e.target.value)} /><br/>
      <input placeholder='Admin email' value={email} onChange={e=>setEmail(e.target.value)} /><br/>
      <input type='password' placeholder='Password' value={password} onChange={e=>setPassword(e.target.value)} /><br/>
      <input placeholder='Store URL (optional)' value={storeUrl} onChange={e=>setStoreUrl(e.target.value)} /><br/>
      <button onClick={signup}>Signup</button>
      <hr/>
      <h3>Or login</h3>
      <input placeholder='tenantId (optional quick login)' value={tenantId} onChange={e=>setTenantId(e.target.value)} /><br/>
      <input placeholder='email' value={email} onChange={e=>setEmail(e.target.value)} /><br/>
      <input type='password' placeholder='password' value={password} onChange={e=>setPassword(e.target.value)} /><br/>
      <button onClick={quickLogin}>Login</button>
    </div>
  );
}
