import React, { useEffect, useState, useCallback } from 'react';
import { useNavigate } from 'react-router-dom';
import { jwtDecode } from 'jwt-decode';
import API from '../api';
import SalesTrendChart from '../components/SalesTrendChart';
import TopCustomersBarChart from '../components/TopCustomersBarChart';

// --- Add MUI imports ---
import { Box, Grid, Skeleton } from '@mui/material';

export default function DashboardPage() {
  const [tenantInfo, setTenantInfo] = useState(null);
  const [overview, setOverview] = useState(null);
  const [eventSummary, setEventSummary] = useState(null);
  const [topCustomers, setTopCustomers] = useState([]);
  const [orders, setOrders] = useState([]);
  const [customerHistory, setCustomerHistory] = useState([]);
  const [error, setError] = useState('');
  const [loading, setLoading] = useState(true);
  const [ingesting, setIngesting] = useState(false);
  const [startDate, setStartDate] = useState('');
  const [endDate, setEndDate] = useState('');
  const [salesTrend, setSalesTrend] = useState([]);
  const [topCustomersChartData, setTopCustomersChartData] = useState([]);
  const navigate = useNavigate();

  const handleLogout = useCallback(() => {
    localStorage.removeItem('token');
    navigate('/login');
  }, [navigate]);

  const fetchData = useCallback(async () => {
    try {
      setLoading(true);
      const [
        tenantInfoRes, overviewRes, eventSummaryRes, topCustomersRes,
        ordersRes, customerHistoryRes, salesTrendRes, topCustomersChartRes
      ] = await Promise.all([
        API.get('/dashboard/tenant-info'),
        API.get('/dashboard/overview'),
        API.get('/dashboard/events-summary'),
        API.get('/dashboard/top-customers'),
        API.get('/dashboard/orders'),
        API.get('/dashboard/customer-products'),
        API.get('/dashboard/sales-trend'),
        API.get('/dashboard/top-customers-chart')
      ]);

      setTenantInfo(tenantInfoRes.data);
      setOverview(overviewRes.data);
      setEventSummary(eventSummaryRes.data);
      setTopCustomers(topCustomersRes.data.top);
      setOrders(ordersRes.data.orders);
      setCustomerHistory(customerHistoryRes.data);
      setSalesTrend(salesTrendRes.data);
      setTopCustomersChartData(topCustomersChartRes.data);
      setError('');
    } catch (err) {
      setError('Failed to fetch dashboard data.');
      if (err.response?.status === 401) handleLogout();
    } finally {
      setLoading(false);
    }
  }, [handleLogout]);

  useEffect(() => { fetchData(); }, [fetchData]);

  // --- Skeleton UI while loading ---
  if (loading) {
    return (
      <Box sx={{ padding: 3 }}>
        <Grid container spacing={3}>
          {/* Skeleton for KPI cards */}
          <Grid item xs={12} sm={6} md={3}>
            <Skeleton variant="rectangular" height={100} />
          </Grid>
          <Grid item xs={12} sm={6} md={3}>
            <Skeleton variant="rectangular" height={100} />
          </Grid>
          <Grid item xs={12} sm={6} md={3}>
            <Skeleton variant="rectangular" height={100} />
          </Grid>
          <Grid item xs={12} sm={6} md={3}>
            <Skeleton variant="rectangular" height={100} />
          </Grid>

          {/* Skeleton for Sales Trend chart */}
          <Grid item xs={12} md={8}>
            <Skeleton variant="rectangular" height={300} />
          </Grid>

          {/* Skeleton for Top Customers chart */}
          <Grid item xs={12} md={4}>
            <Skeleton variant="rectangular" height={300} />
          </Grid>

          {/* Skeleton for Orders table */}
          <Grid item xs={12}>
            <Skeleton variant="rectangular" height={200} />
          </Grid>

          {/* Skeleton for Customer History table */}
          <Grid item xs={12}>
            <Skeleton variant="rectangular" height={200} />
          </Grid>
        </Grid>
      </Box>
    );
  }

  const averageOrderValue = (overview && overview.totalOrders > 0)
    ? (overview.revenue / overview.totalOrders)
    : 0;

  return (
    <div style={{ padding: '20px' }}>
      {/* Header */}
      <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', marginBottom: '20px' }}>
        <div style={{ display: 'flex', alignItems: 'center', gap: '15px' }}>
          {tenantInfo && tenantInfo.logoUrl && (
            <img 
              src={tenantInfo.logoUrl} 
              alt={`${tenantInfo.name} Logo`} 
              style={{ height: '50px', width: '50px', objectFit: 'contain' }} 
            />
          )}
          <div>
            <h2 style={{ margin: 0 }}>
              {tenantInfo ? tenantInfo.name : 'Dashboard'}
            </h2>
            <p style={{ margin: '5px 0', color: '#555' }}>
              Owner: {tenantInfo ? tenantInfo.adminEmail : '...'}
            </p>
          </div>
        </div>
        <button onClick={handleLogout} style={{ padding: '8px 12px', width: 'auto' }}>
          Logout
        </button>
      </div>

      {overview && overview.totalCustomers === 0 && (
        <div style={{ padding: '20px', margin: '20px 0', backgroundColor: '#eef', border: '1px solid #ccf' }}>
          <h4>Your dashboard is empty!</h4>
          <p>Click the button below to load some sample customer and order data.</p>
          <button onClick={handleIngestData} disabled={ingesting} style={{ width: 'auto' }}>
            {ingesting ? 'Ingesting...' : 'Load Sample Data'}
          </button>
        </div>
      )}
      {error && <div style={{ color: 'red', margin: '10px 0' }}>{error}</div>}

      {/* Overview Section */}
      <div className="widget">
        <h3>Overview</h3>
        {overview && (
          <div>
            <div>Total Customers: {overview.totalCustomers}</div>
            <div>Total Orders: {overview.totalOrders}</div>
            <div>Revenue: ₹{parseFloat(overview.revenue).toFixed(2)}</div>
            <div>Average Order Value: ₹{averageOrderValue.toFixed(2)}</div>
          </div>
        )}
      </div>

      {/* Sales Trend */}
      <div className="widget">
        <h3>Sales Trend (Last 30 Days)</h3>
        <SalesTrendChart data={salesTrend} />
      </div>

      {/* Events Summary */}
      <div className="widget">
        <h3>Activity</h3>
        {eventSummary && (
          <div>
            <div>Checkouts Started: {eventSummary.checkout_started || 0}</div>
            <div>Abandoned Carts: {eventSummary.cart_abandoned || 0}</div>
          </div>
        )}
      </div>

      {/* Top Customers */}
      <div className="widget">
        <h3>Top 5 Customers by Spend</h3>
        <TopCustomersBarChart data={topCustomersChartData} />
      </div>

      {/* Orders */}
      <div className="widget">
        <h3>Orders by Date</h3>
        <div style={{ display: 'flex', alignItems: 'center', gap: '10px', marginBottom: '20px' }}>
          <label>From:</label>
          <input type="date" value={startDate} onChange={e => setStartDate(e.target.value)} />
          <label>To:</label>
          <input type="date" value={endDate} onChange={e => setEndDate(e.target.value)} />
          <button onClick={handleFilterOrders} style={{ width: 'auto' }}>Filter</button>
        </div>
        <table border="1" cellPadding="8" style={{ borderCollapse: 'collapse', width: '100%', marginTop: '20px' }}>
          <thead>
            <tr>
              <th>Order ID</th>
              <th>Products</th>
              <th>Total Price</th>
              <th>Date</th>
            </tr>
          </thead>
          <tbody>
            {orders.map((order) => (
              <tr key={order.id}>
                <td>{order.shopifyId}</td>
                <td>
                  {order.Products && order.Products.length > 0
                    ? order.Products.map(p => p.title).join(', ')
                    : 'N/A'}
                </td>
                <td>₹{parseFloat(order.totalPrice).toFixed(2)}</td>
                <td>{new Date(order.createdAtShopify).toLocaleDateString()}</td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>

      {/* Customer History */}
      <div className="widget">
        <h3>Customer Purchase History</h3>
        <table border="1" cellPadding="8" style={{ borderCollapse: 'collapse', width: '100%', marginTop: '20px' }}>
          <thead>
            <tr>
              <th>Customer</th>
              <th>Email</th>
              <th>Order IDs</th>
            </tr>
          </thead>
          <tbody>
            {customerHistory.map((customer) => (
              <tr key={customer.id}>
                <td>{customer.name}</td>
                <td>{customer.email}</td>
                <td>
                  {customer.orders.length > 0
                    ? <ul>{customer.orders.map(orderId => <li key={orderId}>{orderId}</li>)}</ul>
                    : 'No orders found'}
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
    </div>
  );
}
