        { shopifyId: 1007, totalPrice: "135.00", createdAtShopify: "2025-09-04T14:55:00Z", customer: { id: 206 }, line_items: [{ product_id: 102, quantity: 1, price: "75.00" }, { product_id: 105, quantity: 1, price: "60.00" }] },
