import React, { useState, useEffect } from 'react';
import { useOutletContext } from 'react-router-dom';
import TopCustomersBarChart from '../components/TopCustomersBarChart';
import API from '../api';

export default function TopCustomersPage() {
  const { dataSource } = useOutletContext(); // fetch from DashboardLayout
  const [topCustomersChartData, setTopCustomersChartData] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');

  useEffect(() => {
    const fetchTopCustomers = async () => {
      try {
        setLoading(true);
        setError('');

        if (dataSource === 'shopify') {
          const [customersRes, ordersRes] = await Promise.all([
            API.get('/shopify/sync/customers'),
            API.get('/shopify/sync/orders')
          ]);

          const customers = customersRes.data.data || [];
          const orders = ordersRes.data.data || [];

          // Map customerId to total spend
          const customerSpendMap = {};
          orders.forEach(order => {
            const customerId = order.customer?.id;
            if (!customerId) return;

            if (!customerSpendMap[customerId]) {
              const customer = customers.find(c => c.id === customerId);
              customerSpendMap[customerId] = {
                name: customer ? `${customer.first_name} ${customer.last_name}` : 'Unknown',
                spend: 0
              };
            }

            customerSpendMap[customerId].spend += parseFloat(order.total_price || 0);
          });

          const top5 = Object.values(customerSpendMap)
            .sort((a, b) => b.spend - a.spend)
            .slice(0, 5)
            .map(c => ({
              name: c.name || 'Unknown',
              spend: parseFloat(c.spend.toFixed(2)) || 0
            }));

          setTopCustomersChartData(top5);
        } else {
          // fallback: sample data if dataSource is 'sample'
          setTopCustomersChartData([
            { name: "Customer A", spend: 200 },
            { name: "Customer B", spend: 180 },
            { name: "Customer C", spend: 150 },
            { name: "Customer D", spend: 120 },
            { name: "Customer E", spend: 100 }
          ]);
        }
      } catch (err) {
        console.error(err);
        setError('Failed to fetch top customers.');
        setTopCustomersChartData([]);
      } finally {
        setLoading(false);
      }
    };

    fetchTopCustomers();
  }, [dataSource]);

  if (loading) return <div>Loading top customers...</div>;
  if (error) return <div style={{ color: 'red' }}>{error}</div>;

  return (
    <div className="widget">
      <h3>Top 5 Customers by Spend</h3>
      <TopCustomersBarChart data={topCustomersChartData} xKey="name" yKey="spend" />
    </div>
  );
}
