import React, { useEffect, useState } from 'react';
import { Outlet, Link, useNavigate } from 'react-router-dom';
import API from '../api';

export default function DashboardLayout() {
  const navigate = useNavigate();
  const [dashboardData, setDashboardData] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');

  // --- Sample Data Fallback ---
  const sampleData = {
    totalCustomers: 15,
    totalOrders: 23,
    revenue: 1820
  };

  useEffect(() => {
    async function fetchDashboard() {
      try {
        setLoading(true);
        const token = localStorage.getItem('token');
        if (!token) {
          navigate('/login');
          return;
        }
        const res = await API.get('/dashboard/overview', {
          headers: { Authorization: `Bearer ${token}` }
        });
        setDashboardData(res.data);
      } catch (err) {
        console.error('Failed to fetch dashboard data:', err);
        setError('Failed to fetch dashboard data. Showing sample data.');
        setDashboardData(sampleData); // fallback to sample data
      } finally {
        setLoading(false);
      }
    }
    fetchDashboard();
  }, [navigate]);

  return (
    <div className="flex min-h-screen">
      {/* Sidebar */}
      <aside className="w-64 bg-gray-900 text-white p-4 space-y-4">
        <h1 className="text-xl font-bold">The Wardrobe Room</h1>
        <nav className="flex flex-col space-y-2">
          <Link to="/dashboard" className="hover:underline">Overview</Link>
          <Link to="/dashboard/sales" className="hover:underline">Sales Trend</Link>
          <Link to="/dashboard/orders" className="hover:underline">Orders</Link>
          <Link to="/dashboard/customers" className="hover:underline">Top Customers</Link>
          <Link to="/dashboard/history" className="hover:underline">Customer History</Link>
        </nav>
        <button
          className="mt-4 px-3 py-2 bg-red-600 rounded"
          onClick={() => {
            localStorage.removeItem('token');
            navigate('/login');
          }}
        >
          Logout
        </button>
      </aside>

      {/* Main Content */}
      <main className="flex-1 p-6">
        {loading ? (
          <p className="text-gray-500">Loading dashboard...</p>
        ) : (
          <>
            {error && <p className="text-red-600 mb-2">{error}</p>}

            <div className="grid grid-cols-3 gap-4">
              <div className="bg-white shadow p-4 rounded">
                <h2 className="text-lg font-semibold">Total Customers</h2>
                <p className="text-2xl">{dashboardData?.totalCustomers}</p>
              </div>

              <div className="bg-white shadow p-4 rounded">
                <h2 className="text-lg font-semibold">Total Orders</h2>
                <p className="text-2xl">{dashboardData?.totalOrders}</p>
              </div>

              <div className="bg-white shadow p-4 rounded">
                <h2 className="text-lg font-semibold">Revenue</h2>
                <p className="text-2xl">â‚¹{dashboardData?.revenue}</p>
              </div>
            </div>

            {/* Nested Routes */}
            <Outlet />
          </>
        )}
      </main>
    </div>
  );
}
