import React, { useState, useEffect, useCallback } from 'react';
import { Outlet, Link, useNavigate } from 'react-router-dom';
import API from '../api';
import { jwtDecode } from 'jwt-decode';

export default function DashboardLayout() {
  const [tenantInfo, setTenantInfo] = useState(null);
  const [overview, setOverview] = useState(null);
  const [eventSummary, setEventSummary] = useState(null);
  const [topCustomersChartData, setTopCustomersChartData] = useState([]);
  const [salesTrend, setSalesTrend] = useState([]);
  const [error, setError] = useState('');
  const [loading, setLoading] = useState(true);
  const [loadingMessage, setLoadingMessage] = useState('');
  const [dataSource, setDataSource] = useState('sample'); // Keep this global state
  const navigate = useNavigate();

  const handleLogout = useCallback(() => {
    localStorage.removeItem('token');
    navigate('/login');
  }, [navigate]);

  // This function now ONLY fetches data for the layout itself
  const fetchData = useCallback(async () => {
    // ... (logic to get token and set headers is the same)
    const token = localStorage.getItem('token');
    if (!token) { /* ... */ }
    const headers = { 'Authorization': `Bearer ${token}` };

    try {
      if (!loadingMessage) setLoading(true);

      // --- REMOVED: No longer fetches /dashboard/orders ---
      const [tenantInfoRes, overviewRes, eventSummaryRes, salesTrendRes, topCustomersChartRes] = await Promise.all([
        API.get('/dashboard/tenant-info', { headers }),
        API.get('/dashboard/overview', { headers }),
        API.get('/dashboard/events-summary', { headers }),
        API.get('/dashboard/sales-trend', { headers }),
        API.get('/dashboard/top-customers-chart', { headers })
      ]);

      setTenantInfo(tenantInfoRes.data);
      setOverview(overviewRes.data);
      setEventSummary(eventSummaryRes.data);
      setSalesTrend(salesTrendRes.data);
      setTopCustomersChartData(topCustomersChartRes.data);
      setError('');
    } catch (err) {
      // ... (error handling is the same)
    } finally {
      setLoading(false);
      setLoadingMessage('');
    }
  }, [handleLogout, loadingMessage]);

  useEffect(() => {
    fetchData();
  }, []); // Run only once on initial mount

  const handleSyncShopify = async () => { /* ... (this function is unchanged) ... */ };
  const handleIngestSampleData = async () => { /* ... (this function is unchanged) ... */ };
  const handleUnloadData = async () => { /* ... (this function is unchanged) ... */ };

  if (loading && !loadingMessage) return <h2>Loading dashboard...</h2>;

  return (
    <div style={{ padding: '20px' }}>
      <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', marginBottom: '20px' }}>
        <div style={{ display: 'flex', alignItems: 'center', gap: '15px' }}>
          {/* ... tenant logo and name ... */}
        </div>
        {/* --- ADDED: The toggle buttons are now here in the header --- */}
        <div style={{ display: 'flex', gap: '10px', alignItems: 'center' }}>
          <button onClick={() => setDataSource('sample')} disabled={dataSource === 'sample' || loading}>
            Sample Data
          </button>
          <button onClick={() => setDataSource('shopify')} disabled={dataSource === 'shopify' || loading}>
            Shopify Data
          </button>
          <button onClick={handleLogout}>Logout</button>
        </div>
      </div>
      
      {/* ... (Your nav links and error display) ... */}

      {/* --- MODIFIED: Pass dataSource and the refetch function to children --- */}
      <Outlet context={{ overview, eventSummary, topCustomersChartData, salesTrend, dataSource, fetchData }} />
    </div>
  );
}
