import React, { useEffect, useState, useCallback } from 'react';
import { useNavigate } from 'react-router-dom';
import { jwtDecode } from 'jwt-decode';
import API from '../api';

export default function DashboardPage() {
  const [overview, setOverview] = useState(null);
  const [topCustomers, setTopCustomers] = useState([]);
  const [orders, setOrders] = useState([]);
  const [error, setError] = useState('');
  const [loading, setLoading] = useState(true);
  const [ingesting, setIngesting] = useState(false);
  const [startDate, setStartDate] = useState('');
  const [endDate, setEndDate] = useState('');

  const navigate = useNavigate();

  const handleLogout = useCallback(() => {
    localStorage.removeItem('token');
    navigate('/login');
  }, [navigate]);

  const fetchData = useCallback(async () => {
    try {
      setLoading(true);
      const overviewRes = await API.get('/dashboard/overview');
      const topCustomersRes = await API.get('/dashboard/top-customers');
      const ordersRes = await API.get('/dashboard/orders');

      setOverview(overviewRes.data);
      setTopCustomers(topCustomersRes.data.top);
      setOrders(ordersRes.data.orders);
      setCustomerProducts(customerProductsRes.data);
      setError('');
    } catch (err) {
      setError('Failed to fetch dashboard data.');
      if (err.response?.status === 401) handleLogout();
    } finally {
      setLoading(false);
    }
  }, [handleLogout]);

  useEffect(() => {
    fetchData();
  }, [fetchData]);

  // --- MODIFIED: Updated sample data to include products and line items ---
  const handleIngestData = async () => {
    try {
      setIngesting(true);
      setError('');
      const token = localStorage.getItem('token');
      const { tenantId } = jwtDecode(token);

      // 1. Ingest Products first
      await API.post(`/ingest/tenant/${tenantId}/products`, [
        { "id": 78901, "title": "Classic T-Shirt", "price": "25.00" },
        { "id": 78902, "title": "Denim Jeans", "price": "75.50" }
      ]);

      // 2. Ingest Customers
      await API.post(`/ingest/tenant/${tenantId}/customers`, [
        { "id": 89201, "first_name": "John", "last_name": "Doe", "email": "john.doe@example.com" },
        { "id": 89202, "first_name": "Jane", "last_name": "Smith", "email": "jane.smith@example.com" }
      ]);

      // 3. Ingest Orders with line_items that link to the products
      await API.post(`/ingest/tenant/${tenantId}/orders`, [
        { 
          "id": 55401, "total_price": "100.50", "created_at": "2025-09-10T10:00:00Z", 
          "customer": { "id": 89201 },
          "line_items": [
            { "product_id": 78901, "quantity": 1, "price": "25.00" },
            { "product_id": 78902, "quantity": 1, "price": "75.50" }
          ]
        },
        { 
          "id": 55402, "total_price": "50.00", "created_at": "2025-09-09T15:30:00Z", 
          "customer": { "id": 89202 },
          "line_items": [
            { "product_id": 78901, "quantity": 2, "price": "25.00" }
          ]
        }
      ]);

      await fetchData();

    } catch (err) {
      setError('Failed to ingest data. Please check the server logs.');
    } finally {
      setIngesting(false);
    }
  };

  const handleFilterOrders = async () => {
    try {
      setError('');
      const params = new URLSearchParams();
      if (startDate) params.append('from', startDate);
      if (endDate) params.append('to', endDate);

      const res = await API.get(`/dashboard/orders?${params.toString()}`);
      setOrders(res.data.orders);
    } catch (err) {
      setError('Failed to filter orders.');
    }
  };

  if (loading) return <div>Loading dashboard...</div>;

  return (
    <div>
      <div style={{ display: 'flex', justifyContent: 'flex-start', alignItems: 'center', gap: '20px' }}>
        <h2>Dashboard</h2>
        <button onClick={handleLogout} style={{ padding: '8px 12px', width: 'auto' }}>
            Logout
        </button>
      </div>

      {overview && overview.totalCustomers === 0 && (
        <div style={{ padding: '20px', margin: '20px 0', backgroundColor: '#eef', border: '1px solid #ccf' }}>
          <h4>Your dashboard is empty!</h4>
          <p>Click the button below to load some sample customer and order data.</p>
          <button onClick={handleIngestData} disabled={ingesting} style={{ width: 'auto' }}>
            {ingesting ? 'Ingesting...' : 'Load Sample Data'}
          </button>
        </div>
      )}
      {error && <div style={{ color: 'red', margin: '10px 0' }}>{error}</div>}

      <h3 style={{ marginTop: 20 }}>Overview</h3>
      {overview && (
        <div>
          <div>Total Customers: {overview.totalCustomers}</div>
          <div>Total Orders: {overview.totalOrders}</div>
          <div>Revenue: ${parseFloat(overview.revenue).toFixed(2)}</div>
        </div>
      )}

      <h3 style={{ marginTop: 20 }}>Top 5 Customers by Spend</h3>
      <table border="1" cellPadding="8" style={{ borderCollapse: 'collapse', width: '100%' }}>
        <thead>
          <tr>
            <th>Name</th>
            <th>Email</th>
            <th>Total Spent</th>
          </tr>
        </thead>
        <tbody>
          {topCustomers.map((customer) => (
            <tr key={customer.id}>
              <td>{customer.firstName} {customer.lastName}</td>
              <td>{customer.email}</td>
              <td>${parseFloat(customer.totalSpent).toFixed(2)}</td>
            </tr>
          ))}
        </tbody>
      </table>

      <div style={{ marginTop: 40 }}>
        <h3>Orders by Date</h3>
        <div style={{ display: 'flex', alignItems: 'center', gap: '10px' }}>
          <label>From:</label>
          <input type="date" value={startDate} onChange={e => setStartDate(e.target.value)} />
          <label>To:</label>
          <input type="date" value={endDate} onChange={e => setEndDate(e.target.value)} />
          <button onClick={handleFilterOrders} style={{ width: 'auto' }}>Filter</button>
        </div>

        {/* --- MODIFIED: Updated this table to show products --- */}
        <table border="1" cellPadding="8" style={{ borderCollapse: 'collapse', width: '100%', marginTop: '20px' }}>
          <thead>
              <tr>
                  <th>Order ID</th>
                  <th>Products</th>
                  <th>Total Price</th>
                  <th>Date</th>
              </tr>
          </thead>
          <tbody>
              {orders.map((order) => (
                  <tr key={order.id}>
                      <td>{order.shopifyId}</td>
                      <td>
                        {order.Products && order.Products.length > 0
                          ? order.Products.map(p => p.title).join(', ')
                          : 'N/A'}
                      </td>
                      <td>${parseFloat(order.totalPrice).toFixed(2)}</td>
                      <td>{new Date(order.createdAtShopify).toLocaleDateString()}</td>
                  </tr>
              ))}
          </tbody>
        </table>
      </div>

      <div>
      {/* ... (Header, Overview, Top Customers, and Orders by Date sections are all the same) ... */}

      {/* --- NEW SECTION: Customer Purchase History --- */}
      <div style={{ marginTop: 40 }}>
        <h3>Customer Purchase History</h3>
        <table border="1" cellPadding="8" style={{ borderCollapse: 'collapse', width: '100%', marginTop: '20px' }}>
          <thead>
            <tr>
              <th>Customer</th>
              <th>Email</th>
              <th>Products Purchased</th>
            </tr>
          </thead>
          <tbody>
            {customerProducts.map((customer) => (
              <tr key={customer.id}>
                <td>{customer.name}</td>
                <td>{customer.email}</td>
                <td>
                  {customer.products.length > 0
                    ? <ul>{customer.products.map(p => <li key={p}>{p}</li>)}</ul>
                    : 'No products purchased'}
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>

    </div>
    </div>


  );
}
