import React, { useState } from 'react';
import { useOutletContext } from 'react-router-dom';
import API from '../api';

export default function OrdersPage() {
  const { orders: initialOrders } = useOutletContext();
  const [orders, setOrders] = useState(initialOrders);
  const [startDate, setStartDate] = useState('');
  const [endDate, setEndDate] = useState('');
  const [error, setError] = useState('');

  const handleFilterOrders = async () => {
    try {
      setError('');
      const params = new URLSearchParams();
      if (startDate) params.append('from', startDate);
      if (endDate) params.append('to', endDate);
      const res = await API.get(`/dashboard/orders?${params.toString()}`);
      setOrders(res.data.orders);
    } catch (err) {
      setError('Failed to filter orders.');
    }
  };

  return (
    <div className="widget">
      <h3>Orders by Date</h3>
      <div style={{ display: 'flex', alignItems: 'center', gap: '10px', marginBottom: '20px' }}>
        <label>From:</label>
        <input type="date" value={startDate} onChange={e => setStartDate(e.target.value)} />
        <label>To:</label>
        <input type="date" value={endDate} onChange={e => setEndDate(e.target.value)} />
        <button onClick={handleFilterOrders} style={{ width: 'auto' }}>Filter</button>
      </div>
      {error && <div style={{ color: 'red', marginBottom: '10px' }}>{error}</div>}
      <table border="1" cellPadding="8" style={{ borderCollapse: 'collapse', width: '100%' }}>
        <thead>
          <tr>
            <th>Order ID</th>
            <th>Products</th>
            <th>Total Price</th>
            <th>Date</th>
          </tr>
        </thead>
        <tbody>
          {orders.map(order => (
            <tr key={order.id}>
              <td>{order.shopifyId}</td>
              <td>{order.Products.map(p => p.title).join(', ')}</td>
              <td>â‚¹{parseFloat(order.totalPrice).toFixed(2)}</td>
              <td>{new Date(order.createdAtShopify).toLocaleDateString()}</td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
}
