import React, { useState, useEffect } from 'react';
import { useOutletContext } from 'react-router-dom';
import TopCustomersBarChart from '../components/TopCustomersBarChart';
import API from '../api';

export default function TopCustomersPage() {
  const { topCustomersChartData: initialData, dataSource } = useOutletContext();
  const [topCustomersChartData, setTopCustomersChartData] = useState(initialData || []);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');

  useEffect(() => {
    const fetchTopCustomers = async () => {
      try {
        setLoading(true);
        setError('');

        if (dataSource === 'shopify') {
          // Fetch Shopify customers and orders
          const [customersRes, ordersRes] = await Promise.all([
            API.get('/shopify/sync/customers'),
            API.get('/shopify/sync/orders')
          ]);

          const customers = customersRes.data.data;
          const orders = ordersRes.data.data;

          // Compute total spend per customer
          const customerSpendMap = {};
          orders.forEach(order => {
            const customerId = order.customer?.id;
            if (customerId) {
              if (!customerSpendMap[customerId]) {
                customerSpendMap[customerId] = {
                  name: `${order.customer.first_name} ${order.customer.last_name}`,
                  spend: 0
                };
              }
              customerSpendMap[customerId].spend += parseFloat(order.total_price || 0);
            }
          });

          // Take top 5 customers
          const top5 = Object.values(customerSpendMap)
            .sort((a, b) => b.spend - a.spend)
            .slice(0, 5);

          setTopCustomersChartData(top5);
        } else {
          // Use sample data from Outlet context
          setTopCustomersChartData(initialData);
        }
      } catch (err) {
        console.error(err);
        setError('Failed to fetch top customers.');
      } finally {
        setLoading(false);
      }
    };

    fetchTopCustomers();
  }, [dataSource, initialData]);

  if (loading) return <div>Loading top customers...</div>;
  if (error) return <div style={{ color: 'red' }}>{error}</div>;

  return (
    <div className="widget">
      <h3>Top 5 Customers by Spend</h3>
      <TopCustomersBarChart data={topCustomersChartData} />
    </div>
  );
}
