import React, { useState, useEffect, useCallback } from 'react';
import { Outlet, Link, useNavigate } from 'react-router-dom';
import API from '../api';

export default function DashboardLayout() {
  const [tenantInfo, setTenantInfo] = useState(null);
  const [overview, setOverview] = useState(null);
  const [eventSummary, setEventSummary] = useState(null);
  const [topCustomersChartData, setTopCustomersChartData] = useState([]);
  const [salesTrend, setSalesTrend] = useState([]);
  const [error, setError] = useState('');
  const [loading, setLoading] = useState(true);
  const [dataSource, setDataSource] = useState('sample'); // 'sample' or 'shopify'
  const navigate = useNavigate();

  const handleLogout = useCallback(() => {
    localStorage.removeItem('token');
    navigate('/login');
  }, [navigate]);

  const fetchData = useCallback(async () => {
    try {
      setLoading(true);
      let overviewRes, eventSummaryRes, salesTrendRes, topCustomersChartRes;

      if (dataSource === 'sample') {
        [
          overviewRes,
          eventSummaryRes,
          salesTrendRes,
          topCustomersChartRes
        ] = await Promise.all([
          API.get('/dashboard/overview'),
          API.get('/dashboard/events-summary'),
          API.get('/dashboard/sales-trend'),
          API.get('/dashboard/top-customers-chart')
        ]);
      } else if (dataSource === 'shopify') {
        const [productsRes, customersRes, ordersRes] = await Promise.all([
          API.get('/shopify/sync/products'),
          API.get('/shopify/sync/customers'),
          API.get('/shopify/sync/orders')
        ]);

        overviewRes = {
          data: {
            totalCustomers: customersRes.data.data.length,
            totalOrders: ordersRes.data.data.length,
            revenue: ordersRes.data.data.reduce(
              (sum, order) => sum + parseFloat(order.total_price || 0),
              0
            )
          }
        };

        eventSummaryRes = { data: {} };

        // Sales trend (last 30 days)
        const today = new Date();
        const past30Days = Array.from({ length: 30 }).map((_, i) => {
          const date = new Date(today);
          date.setDate(today.getDate() - i);
          const dateStr = date.toISOString().slice(0, 10);
          const dailyRevenue = ordersRes.data.data
            .filter(o => o.created_at.slice(0, 10) === dateStr)
            .reduce((sum, o) => sum + parseFloat(o.total_price || 0), 0);
          return { date: dateStr, revenue: dailyRevenue };
        }).reverse();
        salesTrendRes = { data: past30Days };

        // Top 5 customers
        const topCustomersMap = {};
        ordersRes.data.data.forEach(order => {
          const customer = order.customer;
          if (customer?.id) {
            if (!topCustomersMap[customer.id]) {
              topCustomersMap[customer.id] = {
                id: customer.id,
                name: `${customer.first_name} ${customer.last_name}`,
                spent: 0
              };
            }
            topCustomersMap[customer.id].spent += parseFloat(order.total_price || 0);
          }
        });
        topCustomersChartRes = {
          data: Object.values(topCustomersMap)
            .sort((a, b) => b.spent - a.spent)
            .slice(0, 5)
        };
      }

      const tenantInfoRes = await API.get('/dashboard/tenant-info');

      setTenantInfo(tenantInfoRes.data);
      setOverview(overviewRes.data);
      setEventSummary(eventSummaryRes.data);
      setSalesTrend(salesTrendRes.data);
      setTopCustomersChartData(topCustomersChartRes.data);
      setError('');
    } catch (err) {
      setError('Failed to fetch dashboard data.');
      if (err.response?.status === 401) handleLogout();
      console.error(err);
    } finally {
      setLoading(false);
    }
  }, [dataSource, handleLogout]);

  // --- Added this useEffect to refresh data whenever dataSource changes ---
  useEffect(() => {
    fetchData();
  }, [dataSource, fetchData]);

  if (loading) return <h2>Loading dashboard...</h2>;

  return (
    <div style={{ padding: '20px' }}>
      {/* Header */}
      <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', marginBottom: '20px' }}>
        <div style={{ display: 'flex', alignItems: 'center', gap: '15px' }}>
          {tenantInfo?.logoUrl && (
            <img src={tenantInfo.logoUrl} alt={`${tenantInfo.name} Logo`} style={{ height: '50px', width: '50px', objectFit: 'contain' }} />
          )}
          <div>
            <h2 style={{ margin: 0 }}>{tenantInfo ? tenantInfo.name : 'Dashboard'}</h2>
            <p style={{ margin: '5px 0', color: '#555' }}>Owner: {tenantInfo?.adminEmail || '...'}</p>
          </div>
        </div>
        <div style={{ display: 'flex', gap: '10px', alignItems: 'center' }}>
          <button onClick={() => setDataSource('sample')} disabled={dataSource === 'sample'}>Sample Data</button>
          <button onClick={() => setDataSource('shopify')} disabled={dataSource === 'shopify'}>Shopify Data</button>
          <button onClick={handleLogout} style={{ padding: '8px 12px', width: 'auto' }}>Logout</button>
        </div>
      </div>

      {/* Navigation */}
      <nav style={{ margin: '40px 0', display: 'flex', gap: '30px', justifyContent: 'center' }}>
        {['Overview', 'Sales Trend', 'Orders', 'Top Customers', 'Customer History'].map((page, i) => (
          <Link
            key={i}
            to={`/dashboard/${page.toLowerCase().replace(' ', '-')}`}
            style={{
              fontSize: '40px',
              fontWeight: '900',
              border: '3px solid black',
              padding: '20px',
              textAlign: 'center',
              width: '350px',
              textDecoration: 'none',
              color: 'black'
            }}
          >
            {page}
          </Link>
        ))}
      </nav>

      {error && <div style={{ color: 'red', margin: '10px 0' }}>{error}</div>}

      {/* Render Outlet with data */}
      <Outlet context={{ overview, eventSummary, topCustomersChartData, salesTrend, dataSource }} />
    </div>
  );
}
