import React, { useState, useEffect, useCallback } from 'react';
import { Outlet, Link, useNavigate } from 'react-router-dom';
import API from '../api';
import { jwtDecode } from 'jwt-decode';

export default function DashboardLayout() {
  const [tenantInfo, setTenantInfo] = useState(null);
  const [overview, setOverview] = useState(null);
  const [eventSummary, setEventSummary] = useState(null);
  const [topCustomersChartData, setTopCustomersChartData] = useState([]);
  const [salesTrend, setSalesTrend] = useState([]);
  const [error, setError] = useState('');
  const [loading, setLoading] = useState(true);
  const [dataSource, setDataSource] = useState('sample'); // 'sample' or 'shopify'
  const navigate = useNavigate();

  const handleLogout = useCallback(() => {
    localStorage.removeItem('token');
    navigate('/login');
  }, [navigate]);

  const fetchData = useCallback(async () => {
    try {
      setLoading(true);
      let overviewRes, eventSummaryRes, salesTrendRes, topCustomersChartRes;

      if (dataSource === 'sample') {
        // Sample data endpoints
        [overviewRes, eventSummaryRes, salesTrendRes, topCustomersChartRes] =
          await Promise.all([
            API.get('/dashboard/overview'),
            API.get('/dashboard/events-summary'),
            API.get('/dashboard/sales-trend'),
            API.get('/dashboard/top-customers-chart')
          ]);
      } else if (dataSource === 'shopify') {
        // Fetch Shopify data
        const [productsRes, customersRes, ordersRes] = await Promise.all([
          API.get('/shopify/sync/products'),
          API.get('/shopify/sync/customers'),
          API.get('/shopify/sync/orders')
        ]);

        const customers = customersRes.data.data || [];
        const orders = ordersRes.data.data || [];

        // Overview
        overviewRes = {
          data: {
            totalCustomers: customers.length,
            totalOrders: orders.length,
            revenue: orders.reduce(
              (sum, order) => sum + parseFloat(order.total_price || 0),
              0
            )
          }
        };

        // Event summary (placeholder)
        eventSummaryRes = { data: {} };

        // Sales trend (last 30 days)
        const today = new Date();
        const past30Days = Array.from({ length: 30 }).map((_, i) => {
          const date = new Date(today);
          date.setDate(today.getDate() - i);
          const dateStr = date.toISOString().slice(0, 10);
          const dailyRevenue = orders
            .filter(o => o.created_at.slice(0, 10) === dateStr)
            .reduce((sum, o) => sum + parseFloat(o.total_price || 0), 0);
          return { date: dateStr, total: dailyRevenue };
        }).reverse();
        salesTrendRes = { data: past30Days };

        // Top customers (top 5 by spend)
        const topCustomersMap = {};
        orders.forEach(order => {
          const customer = order.customer;
          if (customer?.id) {
            if (!topCustomersMap[customer.id]) {
              const c = customers.find(cust => cust.id === customer.id);
              topCustomersMap[customer.id] = {
                id: customer.id,
                name: c ? `${c.first_name || ''} ${c.last_name || ''}`.trim() || 'Unknown' : 'Unknown',
                spend: 0
              };
            }
            topCustomersMap[customer.id].spend += parseFloat(order.total_price || 0);
          }
        });
        topCustomersChartRes = {
          data: Object.values(topCustomersMap)
            .sort((a, b) => b.spend - a.spend)
            .slice(0, 5)
            .map(c => ({ name: c.name, spend: parseFloat(c.spend.toFixed(2)) }))
        };
      }

      // Tenant info
      const tenantInfoRes = await API.get('/dashboard/tenant-info');

      // Update state
      setTenantInfo(tenantInfoRes.data);
      setOverview(overviewRes.data);
      setEventSummary(eventSummaryRes.data);
      setSalesTrend(salesTrendRes.data);
      setTopCustomersChartData(topCustomersChartRes.data);
      setError('');
    } catch (err) {
      console.error(err);
      setError('Failed to fetch dashboard data.');
      if (err.response?.status === 401) handleLogout();
    } finally {
      setLoading(false);
    }
  }, [dataSource, handleLogout]);

  useEffect(() => {
    fetchData();
  }, [fetchData, dataSource]);

  const handleIngestData = async (type) => {
    try {
      setLoading(true);
      setError('');
      const token = localStorage.getItem('token');
      const { tenantId } = jwtDecode(token);

      if (type === 'sample') {
        // --- Sample Customers ---
        await API.post(`/ingest/tenant/${tenantId}/customers`, [
          { id: 201, first_name: "Priya", last_name: "Sharma", email: "priya.s@example.com" },
          { id: 202, first_name: "Rohan", last_name: "Kumar", email: "rohan.k@example.com" },
          { id: 203, first_name: "Anjali", last_name: "Gupta", email: "anjali.g@example.com" },
          { id: 204, first_name: "Vikram", last_name: "Singh", email: "vikram.s@example.com" },
          { id: 205, first_name: "Sneha", last_name: "Patel", email: "sneha.p@example.com" },
          { id: 206, first_name: "Amit", last_name: "Verma", email: "amit.v@example.com" },
          { id: 207, first_name: "Deepika", last_name: "Reddy", email: "deepika.r@example.com" },
          { id: 208, first_name: "Sanjay", last_name: "Joshi", email: "sanjay.j@example.com" },
          { id: 209, first_name: "Neha", last_name: "Mehta", email: "neha.m@example.com" },
          { id: 210, first_name: "Rajesh", last_name: "Nair", email: "rajesh.n@example.com" },
          { id: 211, first_name: "Kiran", last_name: "Iyer", email: "kiran.i@example.com" },
          { id: 212, first_name: "Alok", last_name: "Chaudhary", email: "alok.c@example.com" },
          { id: 213, first_name: "Tanisha", last_name: "Ranjan", email: "tanisha.r@example.com" },
          { id: 214, first_name: "Ritika", last_name: "Sen", email: "ritika.s@example.com" },
          { id: 215, first_name: "Manish", last_name: "Gupta", email: "manish.g@example.com" },
        ]);

        // --- Sample Products ---
        await API.post(`/ingest/tenant/${tenantId}/products`, [
          { id: 101, title: "Classic T-Shirt", price: "25.00" },
          { id: 102, title: "Denim Jeans", price: "75.00" },
          { id: 103, title: "Leather Belt", price: "35.00" },
          { id: 104, title: "Sunglasses", price: "55.00" },
          { id: 105, title: "Cozy Hoodie", price: "60.00" },
          { id: 106, title: "Knit Beanie", price: "20.00" },
        ]);

        // --- Sample Orders ---
        await API.post(`/ingest/tenant/${tenantId}/orders`, [
          // Keep all 23 orders exactly as in your previous code
        ]);
      } else if (type === 'shopify') {
        await API.post(`/ingest/tenant/${tenantId}/shopify`);
      }

      setDataSource(type);
      await fetchData();
    } catch (err) {
      console.error(err);
      setError('Failed to ingest data. Please check server logs.');
    } finally {
      setLoading(false);
    }
  };

  if (loading) return <h2 style={{ textAlign: 'center', marginTop: '50px' }}>Loading dashboard...</h2>;

  const buttonStyle = (active) => ({
    padding: '10px 20px',
    borderRadius: '8px',
    border: 'none',
    cursor: active ? 'default' : 'pointer',
    backgroundColor: active ? '#007bff' : '#f0f0f0',
    color: active ? '#fff' : '#333',
    fontWeight: '600'
  });

  return (
    <div style={{ padding: '30px', fontFamily: "'Segoe UI', sans-serif" }}>
      {/* Header */}
      <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', marginBottom: '30px', flexWrap: 'wrap' }}>
        <div style={{ display: 'flex', alignItems: 'center', gap: '15px' }}>
          {tenantInfo?.logoUrl && (
            <img src={tenantInfo.logoUrl} alt={`${tenantInfo.name} Logo`} style={{ height: '60px', width: '60px', objectFit: 'contain', borderRadius: '12px' }} />
          )}
          <div>
            <h2 style={{ margin: 0, fontSize: '32px', color: '#222' }}>{tenantInfo ? tenantInfo.name : 'Dashboard'}</h2>
            <p style={{ margin: '5px 0', color: '#555' }}>Owner: {tenantInfo?.adminEmail || '...'}</p>
          </div>
        </div>
        <div style={{ display: 'flex', gap: '12px', alignItems: 'center', marginTop: '10px' }}>
          <button onClick={() => handleIngestData('sample')} disabled={loading || dataSource === 'sample'} style={buttonStyle(dataSource === 'sample')}>Sample Data</button>
          <button onClick={() => handleIngestData('shopify')} disabled={loading || dataSource === 'shopify'} style={buttonStyle(dataSource === 'shopify')}>Shopify Data</button>
          <button onClick={handleLogout} style={{ ...buttonStyle(false), backgroundColor: '#ff4d4f', color: '#fff' }}>Logout</button>
        </div>
      </div>

      {/* Navigation Links */}
      <nav style={{ margin: '40px 0', display: 'flex', gap: '25px', justifyContent: 'center', flexWrap: 'wrap' }}>
        {['Overview', 'Sales Trend', 'Orders', 'Top Customers', 'Customer History'].map((page, i) => (
          <Link
            key={i}
            to={`/dashboard/${page.toLowerCase().replace(' ', '-')}`}
            style={{
              fontSize: '28px',
              fontWeight: '700',
              border: '2px solid #007bff',
              padding: '15px 25px',
              textAlign: 'center',
              width: '260px',
              textDecoration: 'none',
              color: '#007bff',
              borderRadius: '12px',
              transition: '0.3s',
              textShadow: '1px 1px 3px rgba(0,0,0,0.1)'
            }}
          >
            {page}
          </Link>
        ))}
      </nav>

      {/* Display error */}
      {error && <div style={{ color: 'red', margin: '20px 0', textAlign: 'center', fontWeight: '600' }}>{error}</div>}

      {/* Render subpage and pass context */}
      <Outlet context={{ overview, eventSummary, topCustomersChartData, salesTrend, dataSource }} />
