        ]);

      } else if (source === 'shopify') {
        // --- Ingest Shopify Data ---
        await API.post(`/ingest/tenant/${tenantId}/shopify`);

        // Fetch Shopify data
        const [productsRes, customersRes, ordersRes] = await Promise.all([
          API.get('/shopify/sync/products'),
          API.get('/shopify/sync/customers'),
          API.get('/shopify/sync/orders')
        ]);

        const customers = customersRes.data.data || [];
        const orders = ordersRes.data.data || [];

        // Overview
        setOverview({
          totalCustomers: customers.length,
          totalOrders: orders.length,
          revenue: orders.reduce((sum, order) => sum + parseFloat(order.total_price || 0), 0)
        });

        // Top 5 customers
        const topMap = {};
        orders.forEach(order => {
          const c = order.customer;
          if (c?.id) {
            if (!topMap[c.id]) {
              const cust = customers.find(cust => cust.id === c.id);
              topMap[c.id] = { id: c.id, name: cust ? `${cust.first_name} ${cust.last_name}` : 'Unknown', spend: 0 };
            }
            topMap[c.id].spend += parseFloat(order.total_price || 0);
          }
        });
        setTopCustomers(Object.values(topMap).sort((a, b) => b.spend - a.spend).slice(0, 5));

        // Sales Trend (last 30 days)
        const today = new Date();
        const trend = Array.from({ length: 30 }).map((_, i) => {
          const date = new Date(today);
          date.setDate(today.getDate() - i);
          const dateStr = date.toISOString().slice(0, 10);
          const dailyRevenue = orders
            .filter(o => o.created_at.slice(0, 10) === dateStr)
            .reduce((sum, o) => sum + parseFloat(o.total_price || 0), 0);
          return { date: dateStr, total: dailyRevenue };
        }).reverse();
        setSalesTrend(trend);
      }

      setError('');
    } catch (err) {
      console.error(err);
      setError(`Failed to ingest ${source} data.`);
    } finally {
      setIngesting(false);
    }
  };

  return (
    <div style={{ padding: '40px', textAlign: 'center' }}>
      <h1>Dashboard</h1>

      {!overview && (
        <div style={{ margin: '20px 0' }}>
          <button onClick={() => handleLoadData('sample')} disabled={ingesting} style={buttonStyle}>
            {ingesting ? 'Loading...' : 'Load Sample Data'}
          </button>
          <button onClick={() => handleLoadData('shopify')} disabled={ingesting} style={buttonStyle}>
            {ingesting ? 'Loading...' : 'Load Shopify Data'}
          </button>

          <div style={{ marginTop: '20px', backgroundColor: '#eef', padding: '20px', borderRadius: '12px' }}>
            <h4>Your dashboard is empty!</h4>
            <p>Click the button above to load sample or Shopify data.</p>
          </div>

          {error && <p style={{ color: 'red' }}>{error}</p>}
        </div>
      )}

      {overview && (
        <div>
          <h2>Overview</h2>
          <p>Total Customers: {overview.totalCustomers}</p>
          <p>Total Orders: {overview.totalOrders}</p>
          <p>Revenue: ₹{overview.revenue.toFixed(2)}</p>

          <h3>Top Customers</h3>
          <ul>
            {topCustomers.map(c => (
              <li key={c.id}>{c.name}: ₹{c.spend.toFixed(2)}</li>
            ))}
          </ul>

          <h3>Sales Trend (last 30 days)</h3>
          <ul>
            {salesTrend.map(s => (
              <li key={s.date}>{s.date}: ₹{s.total.toFixed(2)}</li>
            ))}
          </ul>
        </div>
      )}
    </div>
  );
