import React from 'react';
import { Link, Outlet } from 'react-router-dom';

export default function DashboardLayout({ tenantInfo, onLogout }) {
  return (
    <div style={{ display: 'flex', flexDirection: 'column', minHeight: '100vh' }}>
      
      {/* Header */}
      <header style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', padding: '10px 20px', borderBottom: '1px solid #ccc' }}>
        <div style={{ display: 'flex', alignItems: 'center', gap: '15px' }}>
          {tenantInfo && tenantInfo.logoUrl && (
            <img 
              src={tenantInfo.logoUrl} 
              alt={`${tenantInfo.name} Logo`} 
              style={{ height: '50px', width: '50px', objectFit: 'contain' }} 
            />
          )}
          <div>
            <h2 style={{ margin: 0 }}>{tenantInfo ? tenantInfo.name : 'Dashboard'}</h2>
            <p style={{ margin: 0, color: '#555' }}>Owner: {tenantInfo ? tenantInfo.adminEmail : '...'}</p>
          </div>
        </div>
        <button onClick={onLogout} style={{ padding: '8px 12px' }}>Logout</button>
      </header>

      {/* Navigation Links */}
      <nav style={{ padding: '10px 20px', borderBottom: '1px solid #ccc', backgroundColor: '#f8f8f8' }}>
        <Link to="/dashboard" style={{ marginRight: '15px' }}>Overview</Link>
        <Link to="/orders" style={{ marginRight: '15px' }}>Orders</Link>
        <Link to="/sales-trend" style={{ marginRight: '15px' }}>Sales Trend</Link>
        <Link to="/customer-history" style={{ marginRight: '15px' }}>Customer History</Link>
        <Link to="/top-customers">Top Customers</Link>
      </nav>

      {/* Page Content */}
      <main style={{ flex: 1, padding: '20px' }}>
        <Outlet />
      </main>

    </div>
  );
}
