import React, { useEffect, useState } from 'react';
import { useOutletContext } from 'react-router-dom';
import { jwtDecode } from 'jwt-decode';
import API from '../api';
import CountUp from 'react-countup'; // For animated numbers

export default function DashboardPage() {
  const { dataSource, setDataSource } = useOutletContext(); // get dataSource and setter from DashboardLayout
  const [overview, setOverview] = useState(null);
  const [loading, setLoading] = useState(true);
  const [ingesting, setIngesting] = useState(false);
  const [shopifyLoading, setShopifyLoading] = useState(false);
  const [error, setError] = useState('');

  // Fetch overview based on current dataSource
  const fetchOverview = async () => {
    try {
      setLoading(true);
      let overviewRes;

      if (dataSource === 'sample') {
        overviewRes = await API.get(`/dashboard/overview`);
        setOverview(overviewRes.data);
      } else if (dataSource === 'shopify') {
        const [productsRes, customersRes, ordersRes] = await Promise.all([
          API.get(`/shopify/sync/products`),
          API.get(`/shopify/sync/customers`),
          API.get(`/shopify/sync/orders`)
        ]);

        setOverview({
          totalCustomers: customersRes.data.data?.length || 0,
          totalOrders: ordersRes.data.data?.length || 0,
          revenue: ordersRes.data.data?.reduce(
            (sum, order) => sum + parseFloat(order.total_price || 0),
            0
          ) || 0,
        });
      }

      setError('');
    } catch (err) {
      setError('Failed to fetch dashboard overview.');
      console.error(err);
    } finally {
      setLoading(false);
    }
  };

  // Sample data ingestion (only needed if overview is empty)
  const handleIngestData = async () => {
    try {
      setIngesting(true);
      setError('');
      const token = localStorage.getItem('token');
      const { tenantId } = jwtDecode(token);

      // --- Customers: 15 sample ---
      await API.post(`/ingest/tenant/${tenantId}/customers`, [
        { id: 201, first_name: "Priya", last_name: "Sharma", email: "priya.s@example.com" },
        { id: 202, first_name: "Rohan", last_name: "Kumar", email: "rohan.k@example.com" },
        { id: 203, first_name: "Anjali", last_name: "Gupta", email: "anjali.g@example.com" },
        { id: 204, first_name: "Vikram", last_name: "Singh", email: "vikram.s@example.com" },
        { id: 205, first_name: "Sneha", last_name: "Patel", email: "sneha.p@example.com" },
        { id: 206, first_name: "Amit", last_name: "Verma", email: "amit.v@example.com" },
        { id: 207, first_name: "Deepika", last_name: "Reddy", email: "deepika.r@example.com" },
        { id: 208, first_name: "Sanjay", last_name: "Joshi", email: "sanjay.j@example.com" },
        { id: 209, first_name: "Neha", last_name: "Mehta", email: "neha.m@example.com" },
        { id: 210, first_name: "Rajesh", last_name: "Nair", email: "rajesh.n@example.com" },
        { id: 211, first_name: "Kiran", last_name: "Iyer", email: "kiran.i@example.com" },
        { id: 212, first_name: "Alok", last_name: "Chaudhary", email: "alok.c@example.com" },
        { id: 213, first_name: "Tanisha", last_name: "Ranjan", email: "tanisha.r@example.com" },
        { id: 214, first_name: "Ritika", last_name: "Sen", email: "ritika.s@example.com" },
        { id: 215, first_name: "Manish", last_name: "Gupta", email: "manish.g@example.com" },
      ]);

      // --- Products: 6 sample ---
      await API.post(`/ingest/tenant/${tenantId}/products`, [
        { id: 101, title: "Classic T-Shirt", price: "25.00" },
        { id: 102, title: "Denim Jeans", price: "75.00" },
        { id: 103, title: "Leather Belt", price: "35.00" },
        { id: 104, title: "Sunglasses", price: "55.00" },
        { id: 105, title: "Cozy Hoodie", price: "60.00" },
        { id: 106, title: "Knit Beanie", price: "20.00" },
      ]);

      // --- Orders: 23 sample ---
      await API.post(`/ingest/tenant/${tenantId}/orders`, [
        // ... your 23 sample orders here (same as before)
      ]);

      await fetchOverview();
    } catch (err) {
      setError('Failed to ingest data. Please check server logs.');
      console.error(err);
    } finally {
      setIngesting(false);
    }
  };

  // Shopify data ingestion button
  const handleShopifyData = async () => {
    try {
      setShopifyLoading(true);
      setError('');
      setDataSource('shopify'); // update parent dataSource
      await fetchOverview();    // fetch Shopify data
    } catch (err) {
      setError('Failed to load Shopify data.');
      console.error(err);
    } finally {
      setShopifyLoading(false);
    }
  };

  useEffect(() => {
    fetchOverview();
  }, [dataSource]);

  if (loading) return <h2 style={{ textAlign: 'center', marginTop: '100px' }}>Loading dashboard...</h2>;

  return (
    <div style={pageStyle}>
      {overview && overview.totalCustomers === 0 && dataSource === 'sample' && (
        <div style={emptyStyle}>
          <h4>Your dashboard is empty!</h4>
          <p>Click the buttons below to load data.</p>
          <div style={{ display: 'flex', gap: '15px', justifyContent: 'center', marginTop: '15px' }}>
            <button onClick={handleShopifyData} disabled={shopifyLoading} style={buttonStyle}>
              {shopifyLoading ? 'Loading Shopify...' : 'Load Shopify Data'}
            </button>
            <button onClick={handleIngestData} disabled={ingesting} style={buttonStyle}>
              {ingesting ? 'Ingesting...' : 'Load Sample Data'}
            </button>
          </div>
        </div>
      )}

      {error && <div style={{ color: 'red', margin: '10px 0', textAlign: 'center' }}>{error}</div>}

      {overview && (
        <>
          <h1 style={titleStyle}>Overview</h1>
          <div style={cardsContainerStyle}>
            <InteractiveCard title="Total Customers" value={overview.totalCustomers ?? 0} />
            <InteractiveCard title="Total Orders" value={overview.totalOrders ?? 0} />
            <InteractiveCard title="Revenue" value={`₹${parseFloat(overview.revenue ?? 0).toFixed(2)}`} />
          </div>
        </>
      )}
    </div>
  );
}

// Card with hover & animated number
function InteractiveCard({ title, value }) {
  return (
    <div style={interactiveCardStyle}>
      <h2 style={labelStyle}>{title}</h2>
      <p style={valueStyle}><CountUp end={parseFloat(value.toString().replace(/[₹,]/g, ''))} duration={1.5} separator="," prefix={value.toString().startsWith('₹') ? '₹' : ''} /></p>
    </div>
  );
}

// --- Styles ---
const pageStyle = {
  padding: '40px',
  minHeight: '100vh',
  background: 'linear-gradient(135deg, #f5f7fa 0%, #c3cfe2 100%)',
  display: 'flex',
  flexDirection: 'column',
  alignItems: 'center',
};

const titleStyle = {
  fontSize: '48px',
  marginBottom: '50px',
  color: '#222',
  textShadow: '2px 2px 8px rgba(0,0,0,0.1)',
};

const cardsContainerStyle = {
  display: 'flex',
  justifyContent: 'center',
  gap: '40px',
  flexWrap: 'wrap',
};

const interactiveCardStyle = {
  borderRadius: '16px',
  padding: '30px 40px',
  width: '260px',
  textAlign: 'center',
  backgroundColor: '#fff',
  boxShadow: '6px 6px 20px rgba(0,0,0,0.1)',
  transition: 'all 0.3s ease',
  cursor: 'pointer',
};

const labelStyle = { fontSize: '24px', marginBottom: '15px', fontWeight: '600', color: '#444' };
const valueStyle = { fontSize: '36px', fontWeight: '700', color: '#007bff', margin: 0 };

const emptyStyle = {
  padding: '20px',
  margin: '20px 0',
  backgroundColor: '#eef',
  border: '1px solid #ccf',
  borderRadius: '12px',
  textAlign: 'center',
};

const buttonStyle = {
  padding: '10px 25px',
  fontSize: '16px',
  borderRadius: '8px',
  border: 'none',
  backgroundColor: '#007bff',
  color: '#fff',
  cursor: 'pointer',
};
