import React, { useState, useEffect, useCallback } from 'react';
import { Outlet, Link, useNavigate } from 'react-router-dom';
import API from '../api'; // fixed import path

export default function DashboardLayout() {
  const [tenantInfo, setTenantInfo] = useState(null);
  const [overview, setOverview] = useState(null);
  const [eventSummary, setEventSummary] = useState(null);
  const [topCustomersChartData, setTopCustomersChartData] = useState([]);
  const [salesTrend, setSalesTrend] = useState([]);
  const [error, setError] = useState('');
  const [loading, setLoading] = useState(true);
  const navigate = useNavigate();

  const handleLogout = useCallback(() => {
    localStorage.removeItem('token');
    navigate('/login');
  }, [navigate]);

  const fetchData = useCallback(async () => {
    try {
      setLoading(true);
      const [
        tenantInfoRes,
        overviewRes,
        eventSummaryRes,
        salesTrendRes,
        topCustomersChartRes
      ] = await Promise.all([
        API.get('/dashboard/tenant-info'),
        API.get('/dashboard/overview'),
        API.get('/dashboard/events-summary'),
        API.get('/dashboard/sales-trend'),
        API.get('/dashboard/top-customers-chart')
      ]);

      setTenantInfo(tenantInfoRes.data);
      setOverview(overviewRes.data);
      setEventSummary(eventSummaryRes.data);
      setSalesTrend(salesTrendRes.data);
      setTopCustomersChartData(topCustomersChartRes.data);
      setError('');
    } catch (err) {
      setError('Failed to fetch dashboard data.');
      if (err.response?.status === 401) handleLogout();
    } finally {
      setLoading(false);
    }
  }, [handleLogout]);

  useEffect(() => { fetchData(); }, [fetchData]);

  if (loading) return <h2>Loading dashboard...</h2>;

  return (
    <div style={{ padding: '20px' }}>
      {/* Navigation Links */}
      <nav style={{ margin: '20px 0', display: 'flex', gap: '25px' }}>
        <Link to="/dashboard" style={{ fontSize: '20px', fontWeight: 'bold', color: '#1976d2', textDecoration: 'none' }}>Overview</Link>
        <Link to="/dashboard/sales-trend" style={{ fontSize: '20px', fontWeight: 'bold', color: '#1976d2', textDecoration: 'none' }}>Sales Trend</Link>
        <Link to="/dashboard/orders" style={{ fontSize: '20px', fontWeight: 'bold', color: '#1976d2', textDecoration: 'none' }}>Orders</Link>
        <Link to="/dashboard/top-customers" style={{ fontSize: '20px', fontWeight: 'bold', color: '#1976d2', textDecoration: 'none' }}>Top Customers</Link>
        <Link to="/dashboard/customer-history" style={{ fontSize: '20px', fontWeight: 'bold', color: '#1976d2', textDecoration: 'none' }}>Customer History</Link>
      </nav>

      {/* Display error */}
      {error && <div style={{ color: 'red', margin: '10px 0' }}>{error}</div>}

      {/* Render subpage */}
      <Outlet context={{ overview, eventSummary, topCustomersChartData, salesTrend }} />
    </div>
  );
}
