import React, { useEffect, useState } from 'react';
import API from '../api';
export default function Dashboard({ token }){
  const [overview, setOverview] = useState(null);
  const [top, setTop] = useState([]);
  useEffect(()=>{
    API.get('/dashboard/overview', { headers: { Authorization: 'Bearer '+token }}).then(r=>setOverview(r.data));
    API.get('/dashboard/top-customers', { headers: { Authorization: 'Bearer '+token }}).then(r=>setTop(r.data.top));
  },[token]);
  return (
    <div style={{marginTop:20}}>
      <h3>Overview</h3>
      {!overview ? <div>Loading...</div> : <div>
        <div>Total Customers: {overview.totalCustomers}</div>
        <div>Total Orders: {overview.totalOrders}</div>
        <div>Revenue: {overview.revenue}</div>
      </div>}
      <h3 style={{marginTop:20}}>Top 5 Customers by Spend</h3>
      <table border="1" cellPadding="6">
        <thead><tr><th>Email</th><th>Name</th><th>Total Spent</th></tr></thead>
        <tbody>
          {top.map(t=>(
            <tr key={t.id}>
              <td>{t.email}</td>
              <td>{t.firstName} {t.lastName}</td>
              <td>{t.totalSpent}</td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
}
