import React, { useState } from 'react';
import { useOutletContext } from 'react-router-dom';
import API from '../api';
import { jwtDecode } from 'jwt-decode';

export default function DashboardPage() {
  const { overview: layoutOverview, dataSource: layoutDataSource } = useOutletContext();
  const [dataSource, setDataSource] = useState(layoutDataSource || 'sample');
  const [ingesting, setIngesting] = useState(false);
  const [error, setError] = useState('');

  const overview = layoutOverview || { totalCustomers: 0, totalOrders: 0, revenue: 0 };

  const handleIngestData = async (type) => {
    try {
      setIngesting(true);
      setError('');
      const token = localStorage.getItem('token');
      const { tenantId } = jwtDecode(token);

      if (type === 'sample') {
        // Sample Customers
        await API.post(`/ingest/tenant/${tenantId}/customers`, [
          { id: 201, first_name: "Priya", last_name: "Sharma", email: "priya.s@example.com" },
          { id: 202, first_name: "Rohan", last_name: "Kumar", email: "rohan.k@example.com" },
          { id: 203, first_name: "Anjali", last_name: "Gupta", email: "anjali.g@example.com" },
          { id: 204, first_name: "Vikram", last_name: "Singh", email: "vikram.s@example.com" },
          { id: 205, first_name: "Sneha", last_name: "Patel", email: "sneha.p@example.com" },
          { id: 206, first_name: "Amit", last_name: "Verma", email: "amit.v@example.com" },
          { id: 207, first_name: "Deepika", last_name: "Reddy", email: "deepika.r@example.com" },
          { id: 208, first_name: "Sanjay", last_name: "Joshi", email: "sanjay.j@example.com" },
          { id: 209, first_name: "Neha", last_name: "Mehta", email: "neha.m@example.com" },
          { id: 210, first_name: "Rajesh", last_name: "Nair", email: "rajesh.n@example.com" },
          { id: 211, first_name: "Kiran", last_name: "Iyer", email: "kiran.i@example.com" },
          { id: 212, first_name: "Alok", last_name: "Chaudhary", email: "alok.c@example.com" },
          { id: 213, first_name: "Tanisha", last_name: "Ranjan", email: "tanisha.r@example.com" },
          { id: 214, first_name: "Ritika", last_name: "Sen", email: "ritika.s@example.com" },
          { id: 215, first_name: "Manish", last_name: "Gupta", email: "manish.g@example.com" },
        ]);

        // Sample Products
        await API.post(`/ingest/tenant/${tenantId}/products`, [
          { id: 101, title: "Classic T-Shirt", price: "25.00" },
          { id: 102, title: "Denim Jeans", price: "75.00" },
          { id: 103, title: "Leather Belt", price: "35.00" },
          { id: 104, title: "Sunglasses", price: "55.00" },
          { id: 105, title: "Cozy Hoodie", price: "60.00" },
          { id: 106, title: "Knit Beanie", price: "20.00" },
        ]);

        // Sample Orders
        await API.post(`/ingest/tenant/${tenantId}/orders`, [ { id: 1001, total_price: "100.00", created_at: "2025-09-10T10:00:00Z", customer: { id: 201 }, line_items: [{ product_id: 101, quantity: 1, price: "25.00" }, { product_id: 102, quantity: 1, price: "75.00" }] },
        { id: 1002, total_price: "50.00", created_at: "2025-09-09T15:30:00Z", customer: { id: 202 }, line_items: [{ product_id: 101, quantity: 2, price: "25.00" }] },
