import React, { useEffect, useState, useCallback } from 'react';
import { jwtDecode } from 'jwt-decode';
import API from '../api';

export default function DashboardPage() {
  const [overview, setOverview] = useState(null);
  const [loading, setLoading] = useState(true);
  const [ingesting, setIngesting] = useState(false);
  const [error, setError] = useState('');
  const [dataSource, setDataSource] = useState('sample'); // 'sample' or 'shopify'

  const fetchOverview = useCallback(async () => {
    try {
      setLoading(true);
      let overviewRes;

      if (dataSource === 'sample') {
        overviewRes = await API.get(`/dashboard/overview`);
      } else if (dataSource === 'shopify') {
        // Fetch Shopify data
        const [productsRes, customersRes, ordersRes] = await Promise.all([
          API.get(`/shopify/sync/products`),
          API.get(`/shopify/sync/customers`),
          API.get(`/shopify/sync/orders`)
        ]);

        // Build overview from Shopify data
        overviewRes = {
          data: {
            totalCustomers: customersRes.data.data.length,
            totalOrders: ordersRes.data.data.length,
            revenue: ordersRes.data.data.reduce(
              (sum, order) => sum + parseFloat(order.total_price || 0),
              0
            ),
          },
        };
      }

      setOverview(overviewRes.data);
      setError('');
    } catch (err) {
      setError('Failed to fetch dashboard overview.');
      console.error(err);
    } finally {
      setLoading(false);
    }
  }, [dataSource]);

  useEffect(() => {
    fetchOverview();
  }, [fetchOverview]);

  const handleIngestData = async () => {
    try {
      setIngesting(true);
      setError('');
      const token = localStorage.getItem('token');
      const { tenantId } = jwtDecode(token);

      // --- Customers: 15 sample ---
      await API.post(`/ingest/tenant/${tenantId}/customers`, [
        { id: 201, first_name: "Priya", last_name: "Sharma", email: "priya.s@example.com" },
        { id: 202, first_name: "Rohan", last_name: "Kumar", email: "rohan.k@example.com" },
        { id: 203, first_name: "Anjali", last_name: "Gupta", email: "anjali.g@example.com" },
        { id: 204, first_name: "Vikram", last_name: "Singh", email: "vikram.s@example.com" },
        { id: 205, first_name: "Sneha", last_name: "Patel", email: "sneha.p@example.com" },
        { id: 206, first_name: "Amit", last_name: "Verma", email: "amit.v@example.com" },
        { id: 207, first_name: "Deepika", last_name: "Reddy", email: "deepika.r@example.com" },
        { id: 208, first_name: "Sanjay", last_name: "Joshi", email: "sanjay.j@example.com" },
        { id: 209, first_name: "Neha", last_name: "Mehta", email: "neha.m@example.com" },
        { id: 210, first_name: "Rajesh", last_name: "Nair", email: "rajesh.n@example.com" },
        { id: 211, first_name: "Kiran", last_name: "Iyer", email: "kiran.i@example.com" },
        { id: 212, first_name: "Alok", last_name: "Chaudhary", email: "alok.c@example.com" },
        { id: 213, first_name: "Tanisha", last_name: "Ranjan", email: "tanisha.r@example.com" },
        { id: 214, first_name: "Ritika", last_name: "Sen", email: "ritika.s@example.com" },
        { id: 215, first_name: "Manish", last_name: "Gupta", email: "manish.g@example.com" },
      ]);

      // --- Products: same 6 products ---
      await API.post(`/ingest/tenant/${tenantId}/products`, [
        { id: 101, title: "Classic T-Shirt", price: "25.00" },
        { id: 102, title: "Denim Jeans", price: "75.00" },
        { id: 103, title: "Leather Belt", price: "35.00" },
        { id: 104, title: "Sunglasses", price: "55.00" },
        { id: 105, title: "Cozy Hoodie", price: "60.00" },
        { id: 106, title: "Knit Beanie", price: "20.00" },
      ]);

      // --- Orders: 23 sample ---
      await API.post(`/ingest/tenant/${tenantId}/orders`, [
        // ... your existing 23 sample orders here ...
      ]);

      await fetchOverview();
    } catch (err) {
      setError('Failed to ingest data. Please check server logs.');
      console.error(err);
    } finally {
      setIngesting(false);
    }
  };

  if (loading) return <h2>Loading dashboard...</h2>;

  return (
    <div style={{ padding: '20px' }}>
      {/* Toggle buttons for data source */}
      <div style={{ marginBottom: '20px', textAlign: 'center' }}>
        <button
          onClick={() => setDataSource('sample')}
          disabled={dataSource === 'sample'}
          style={{ marginRight: '10px' }}
        >
          Sample Data
        </button>
        <button
          onClick={() => setDataSource('shopify')}
          disabled={dataSource === 'shopify'}
        >
          Shopify Data
        </button>
      </div>

      {overview && overview.totalCustomers === 0 && dataSource === 'sample' && (
        <div style={{ padding: '20px', margin: '20px 0', backgroundColor: '#eef', border: '1px solid #ccf' }}>
          <h4>Your dashboard is empty!</h4>
          <p>Click the button below to load some sample customer and order data.</p>
          <button onClick={handleIngestData} disabled={ingesting}>
            {ingesting ? 'Ingesting...' : 'Load Sample Data'}
          </button>
        </div>
      )}

      {error && <div style={{ color: 'red', margin: '10px 0' }}>{error}</div>}

      {overview && (
        <div style={{ marginTop: '30px' }}>
          <h1 style={{ 
            fontSize: '48px', 
            textAlign: 'center', 
            marginBottom: '40px',
            border: '3px solid black',
            borderRadius: '12px',
            padding: '15px',
            backgroundColor: '#f8f9fa',
            boxShadow: '4px 4px 10px rgba(0,0,0,0.15)'
          }}>
            Overview
          </h1>

          <div style={{ 
            display: 'flex', 
            justifyContent: 'center', 
            gap: '30px',
            flexWrap: 'wrap'
          }}>
            <div style={boxStyle}>
              <h2 style={labelStyle}>Total Customers</h2>
              <p style={valueStyle}>{overview.totalCustomers}</p>
            </div>

            <div style={boxStyle}>
              <h2 style={labelStyle}>Total Orders</h2>
              <p style={valueStyle}>{overview.totalOrders}</p>
            </div>

            <div style={boxStyle}>
              <h2 style={labelStyle}>Revenue</h2>
              <p style={valueStyle}>â‚¹{parseFloat(overview.revenue).toFixed(2)}</p>
            </div>
          </div>
        </div>
      )}
    </div>
  );
}

// --- Styles for the Overview boxes ---
const boxStyle = {
  border: '2px solid #333',
  borderRadius: '12px',
  padding: '25px',
  width: '250px',
  textAlign: 'center',
  backgroundColor: '#ffffff',
  boxShadow: '4px 4px 10px rgba(0,0,0,0.1)'
};

const labelStyle = {
  fontSize: '24px',
  marginBottom: '12px',
  fontWeight: '600',
  color: '#222'
};

const valueStyle = {
  fontSize: '32px',
  fontWeight: 'bold',
  margin: 0,
  color: '#007bff'
};
