import React from 'react';
import { BrowserRouter as Router, Routes, Route, Navigate } from 'react-router-dom';
import LoginPage from './pages/LoginPage';
import SignupPage from './pages/SignupPage';
import DashboardPage from './pages/DashboardPage';
import bgImage from './home.jpg'; // import image from src

const PrivateRoute = ({ children }) => {
  const token = localStorage.getItem('token');
  return token ? children : <Navigate to="/login" />;
};

const pageStyle = {
  background: `url(${bgImage}) no-repeat center center fixed`,
  backgroundSize: 'cover',
  height: '100vh',
  display: 'flex',
  justifyContent: 'center',
  alignItems: 'center',
};

function App() {
  return (
    <Router>
      <Routes>
        <Route path="/login" element={<div style={pageStyle}><LoginPage /></div>} />
        <Route path="/signup" element={<div style={pageStyle}><SignupPage /></div>} />
        <Route
          path="/dashboard"
          element={
            <PrivateRoute>
              <DashboardPage />
            </PrivateRoute>
          }
        />
        <Route
          path="/"
          element={<Navigate to={localStorage.getItem('token') ? "/dashboard" : "/login"} />}
        />
        <Route path="*" element={<Navigate to="/" />} />
      </Routes>
    </Router>
  );
}

export default App;
