import React, { useState, useEffect } from 'react';
import { useOutletContext } from 'react-router-dom';
import API from '../api';

export default function OrdersPage() {
  const { dataSource } = useOutletContext();
  const [orders, setOrders] = useState([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');

  useEffect(() => {
    fetchOrders();
  }, [dataSource]); // This hook runs the fetch function

  const fetchOrders = async () => {
    try {
      setLoading(true);
      setError('');
      const token = localStorage.getItem('token');
      const headers = { 'Authorization': `Bearer ${token}` };
      let ordersData = [];

      if (dataSource === 'sample') {
        const res = await API.get('/dashboard/orders', { headers });
        ordersData = res.data.orders || [];
      } else if (dataSource === 'shopify') {
        // First sync the data, then fetch it from our DB
        await API.get('/shopify/sync/orders', { headers });
        const res = await API.get('/dashboard/orders', { headers });
        ordersData = res.data.orders || [];
      }
      setOrders(ordersData);
    } catch (err) {
      setError('Failed to fetch orders.');
      console.error(err);
    } finally {
      setLoading(false);
    }
  };

  if (loading) return <div>Loading orders...</div>;
  if (error) return <div style={{ color: 'red' }}>{error}</div>;

  return (
    <div>
      <h3>Orders</h3>
      <table border="1" style={{ width: '100%', borderCollapse: 'collapse' }}>
        <thead>
          <tr>
            <th>Order ID</th>
            <th>Customer</th>
            <th>Date</th>
            <th>Total</th>
          </tr>
        </thead>
        <tbody>
          {orders.length === 0 ? (
            <tr>
              <td colSpan="4" style={{ textAlign: 'center' }}>No orders found</td>
            </tr>
          ) : (
            orders.map(order => (
              <tr key={order.id}>
                <td>#{order.shopifyId}</td>
                <td>{order.Customer?.firstName || 'N/A'} {order.Customer?.lastName || ''}</td>
                <td>{new Date(order.createdAtShopify).toLocaleDateString()}</td>
                <td>â‚¹{parseFloat(order.totalPrice).toFixed(2)}</td>
              </tr>
            ))
          )}
        </tbody>
      </table>
    </div>
  );
}
