import React, { useState } from 'react';
import { useOutletContext } from 'react-router-dom';
import API from '../api';

export default function CustomerHistoryPage() {
  // Safe destructuring from useOutletContext
  const { customerHistory: initialHistory = [] } = useOutletContext() || {};
  const [customerHistory, setCustomerHistory] = useState(initialHistory);
  const [startDate, setStartDate] = useState('');
  const [endDate, setEndDate] = useState('');
  const [error, setError] = useState('');
  const [loading, setLoading] = useState(false); // optional loading state

  const handleFilterHistory = async () => {
    try {
      setError('');
      setLoading(true);
      const params = new URLSearchParams();
      if (startDate) params.append('from', startDate);
      if (endDate) params.append('to', endDate);
      const res = await API.get(`/dashboard/customer-history?${params.toString()}`);
      setCustomerHistory(res.data.customerHistory || []); // ensure array
    } catch (err) {
      setError('Failed to filter customer history.');
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="widget">
      <h3>Customer History</h3>

      <div style={{ display: 'flex', alignItems: 'center', gap: '10px', marginBottom: '20px' }}>
        <label>From:</label>
        <input type="date" value={startDate} onChange={e => setStartDate(e.target.value)} />
        <label>To:</label>
        <input type="date" value={endDate} onChange={e => setEndDate(e.target.value)} />
        <button onClick={handleFilterHistory} style={{ width: 'auto' }}>Filter</button>
      </div>

      {error && <div style={{ color: 'red', marginBottom: '10px' }}>{error}</div>}
      {loading && <div style={{ marginBottom: '10px' }}>Loading...</div>}

      <table border="1" cellPadding="8" style={{ borderCollapse: 'collapse', width: '100%' }}>
        <thead>
          <tr>
            <th>Customer Name</th>
            <th>Orders</th>
            <th>Total Spent</th>
            <th>Last Order Date</th>
          </tr>
        </thead>
        <tbody>
          {customerHistory.length === 0 ? (
            <tr>
              <td colSpan="4" style={{ textAlign: 'center' }}>No customer history found</td>
            </tr>
          ) : (
            customerHistory.map(customer => (
              <tr key={customer.id}>
                <td>{customer.name}</td>
                <td>{(customer.Orders || []).map(order => order.shopifyId).join(', ')}</td>
                <td>
                  â‚¹{((customer.Orders || []).reduce((sum, order) => sum + parseFloat(order.totalPrice || 0), 0)).toFixed(2)}
                </td>
                <td>{
                  (customer.Orders || []).length > 0
                    ? new Date(customer.Orders[customer.Orders.length - 1].createdAtShopify).toLocaleDateString()
                    : 'N/A'
                }</td>
              </tr>
            ))
          )}
        </tbody>
      </table>
    </div>
  );
}
