import React from 'react';
import { BrowserRouter as Router, Routes, Route, Navigate } from 'react-router-dom';
import LoginPage from './pages/LoginPage';
import SignupPage from './pages/SignupPage';
import DashboardPage from './pages/DashboardPage';
import PrivateRoute from './components/PrivateRoute'; // Import the new component
import bgImage from './image.png';

// Your background style remains unchanged
const pageStyle = {
  background: `url(${bgImage}) no-repeat center center fixed`,
  backgroundSize: 'cover',
  height: '100vh',
  display: 'flex',
  justifyContent: 'center',
  alignItems: 'center',
};

function App() {
  // Check for the token once
  const token = localStorage.getItem('token');

  return (
    <Router>
      <Routes>
        {/* Public Routes with your background style */}
        <Route path="/login" element={<div style={pageStyle}><LoginPage /></div>} />
        <Route path="/signup" element={<div style={pageStyle}><SignupPage /></div>} />

        {/* Your Protected Dashboard Route, now using the imported component */}
        <Route
          path="/dashboard"
          element={
            <PrivateRoute>
              <DashboardPage />
            </PrivateRoute>
          }
        />

        {/* Your Root Path Logic - directs users based on login status */}
        <Route
          path="/"
          element={<Navigate to={token ? "/dashboard" : "/login"} />}
        />

        {/* Catch-all route to handle any other paths */}
        <Route path="*" element={<Navigate to="/" />} />
      </Routes>
    </Router>
  );
}

export default App;
