import React, { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import API from '../api';

export default function DashboardPage() {
  const [overview, setOverview] = useState(null);
  const [topCustomers, setTopCustomers] = useState([]);
  const [error, setError] = useState('');
  const [loading, setLoading] = useState(true);
  const navigate = useNavigate();

  useEffect(() => {
    const fetchData = async () => {
      try {
        setLoading(true);
        // The auth token is now added automatically by the api.js interceptor
        const overviewRes = await API.get('/dashboard/overview');
        const topCustomersRes = await API.get('/dashboard/top-customers');

        setOverview(overviewRes.data);
        setTopCustomers(topCustomersRes.data.top);
        setError('');
      } catch (err) {
        setError('Failed to fetch dashboard data. Please try logging in again.');
        if (err.response?.status === 401) {
            handleLogout(); // Log out if token is invalid
        }
      } finally {
        setLoading(false);
      }
    };
    fetchData();
  }, []);

  const handleLogout = () => {
    localStorage.removeItem('token');
    navigate('/login');
  };

  if (loading) {
    return <div>Loading dashboard...</div>;
  }

  if (error) {
    return <div style={{ color: 'red' }}>{error}</div>;
  }

  return (
    <div>
      <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center' }}>
        <h2>Dashboard</h2>
        <button onClick={handleLogout} style={{ padding: '8px 12px' }}>Logout</button>
      </div>

      <h3 style={{ marginTop: 20 }}>Overview</h3>
      {overview && (
        <div>
          <div>Total Customers: {overview.totalCustomers}</div>
          <div>Total Orders: {overview.totalOrders}</div>
          <div>Revenue: ${parseFloat(overview.revenue).toFixed(2)}</div>
        </div>
      )}

      <h3 style={{ marginTop: 20 }}>Top 5 Customers by Spend</h3>
      <table border="1" cellPadding="8" style={{ borderCollapse: 'collapse', width: '100%' }}>
        <thead>
          <tr>
            <th>Name</th>
            <th>Email</th>
            <th>Total Spent</th>
          </tr>
        </thead>
        <tbody>
          {topCustomers.map((customer) => (
            <tr key={customer.id}>
              <td>{customer.firstName} {customer.lastName}</td>
              <td>{customer.email}</td>
              <td>${parseFloat(customer.totalSpent).toFixed(2)}</td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
}
