import React, { useState, useEffect } from 'react';
import { useOutletContext } from 'react-router-dom';
import API from '../api';

export default function OrdersPage() {
  const { dataSource } = useOutletContext(); // get dataSource from parent layout
  const [orders, setOrders] = useState([]);
  const [startDate, setStartDate] = useState('');
  const [endDate, setEndDate] = useState('');
  const [error, setError] = useState('');
  const [loading, setLoading] = useState(false);

  useEffect(() => {
    fetchOrders();
  }, [dataSource]); // refetch when dataSource changes

  const fetchOrders = async (from, to) => {
    try {
      setError('');
      setLoading(true);
      let ordersData = [];

      if (dataSource === 'sample') {
        // Sample orders endpoint
        const params = new URLSearchParams();
        if (from) params.append('from', from);
        if (to) params.append('to', to);
        const res = await API.get(`/dashboard/orders?${params.toString()}`);
        ordersData = res.data.orders || [];
      } else if (dataSource === 'shopify') {
        // Shopify orders
        const res = await API.get('/shopify/sync/orders');
        ordersData = res.data.data.map(order => ({
          id: order.id,
          shopifyId: order.id,
          Products: order.line_items.map(p => ({ title: p.name })),
          totalPrice: parseFloat(order.total_price || 0),
          createdAtShopify: order.created_at
        }));

        // Filter by date if needed
        if (from || to) {
          const fromDate = from ? new Date(from) : new Date('1970-01-01');
          const toDate = to ? new Date(to) : new Date();
          ordersData = ordersData.filter(order => {
            const orderDate = new Date(order.createdAtShopify);
            return orderDate >= fromDate && orderDate <= toDate;
          });
        }
      }

      setOrders(ordersData);
    } catch (err) {
      console.error(err);
      setError('Failed to fetch orders.');
    } finally {
      setLoading(false);
    }
  };

  const handleFilterOrders = () => {
    fetchOrders(startDate, endDate);
  };

  return (
    <div className="widget">
      <h3>Orders by Date</h3>
      <div style={{ display: 'flex', gap: '10px', marginBottom: '20px' }}>
        <label>From:</label>
        <input type="date" value={startDate} onChange={e => setStartDate(e.target.value)} />
        <label>To:</label>
        <input type="date" value={endDate} onChange={e => setEndDate(e.target.value)} />
        <button onClick={handleFilterOrders}>Filter</button>
      </div>
      {error && <div style={{ color: 'red', marginBottom: '10px' }}>{error}</div>}
      {loading && <div>Loading orders...</div>}
      <table border="1" cellPadding="8" style={{ borderCollapse: 'collapse', width: '100%' }}>
        <thead>
          <tr>
            <th>Order ID</th>
            <th>Products</th>
            <th>Total Price</th>
            <th>Date</th>
          </tr>
        </thead>
        <tbody>
          {orders.length === 0 ? (
            <tr>
              <td colSpan="4" style={{ textAlign: 'center' }}>No orders found</td>
            </tr>
          ) : (
            orders.map(order => (
              <tr key={order.id}>
                <td>{order.shopifyId || order.id}</td>
                <td>{(order.Products || []).map(p => p.title).join(', ')}</td>
                <td>â‚¹{parseFloat(order.totalPrice || 0).toFixed(2)}</td>
                <td>{order.createdAtShopify ? new Date(order.createdAtShopify).toLocaleDateString() : 'N/A'}</td>
              </tr>
            ))
          )}
        </tbody>
      </table>
    </div>
  );
}
