import React, { useEffect, useState } from 'react';
import { useOutletContext } from 'react-router-dom';
import API from '../api';

export default function SalesTrendPage() {
  const { salesTrend, dataSource } = useOutletContext();
  const [trendData, setTrendData] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const fetchData = async () => {
      setLoading(true);
      try {
        if (dataSource === 'sample') {
          setTrendData(salesTrend); // already available from layout
        } else {
          const res = await API.get('/shopify/sync/sales-trend'); // create endpoint on backend
          setTrendData(res.data);
        }
      } catch (err) {
        console.error(err);
      } finally {
        setLoading(false);
      }
    };
    fetchData();
  }, [dataSource, salesTrend]);

  if (loading) return <h2>Loading sales trend...</h2>;

  return (
    <div>
      <h1>Sales Trend ({dataSource})</h1>
      {JSON.stringify(trendData)}
    </div>
  );
}
