import React, { useState, useEffect, useCallback } from 'react';
import { Outlet, Link, useNavigate } from 'react-router-dom';
import API from './api';

export default function DashboardLayout() {
  const [tenantInfo, setTenantInfo] = useState(null);
  const [overview, setOverview] = useState(null);
  const [eventSummary, setEventSummary] = useState(null);
  const [topCustomersChartData, setTopCustomersChartData] = useState([]);
  const [salesTrend, setSalesTrend] = useState([]);
  const [error, setError] = useState('');
  const [loading, setLoading] = useState(true);
  const navigate = useNavigate();

  const handleLogout = useCallback(() => {
    localStorage.removeItem('token');
    navigate('/login');
  }, [navigate]);

  const fetchData = useCallback(async () => {
    try {
      setLoading(true);
      const [
        tenantInfoRes,
        overviewRes,
        eventSummaryRes,
        salesTrendRes,
        topCustomersChartRes
      ] = await Promise.all([
        API.get('/dashboard/tenant-info'),
        API.get('/dashboard/overview'),
        API.get('/dashboard/events-summary'),
        API.get('/dashboard/sales-trend'),
        API.get('/dashboard/top-customers-chart')
      ]);

      setTenantInfo(tenantInfoRes.data);
      setOverview(overviewRes.data);
      setEventSummary(eventSummaryRes.data);
      setSalesTrend(salesTrendRes.data);
      setTopCustomersChartData(topCustomersChartRes.data);
      setError('');
    } catch (err) {
      setError('Failed to fetch dashboard data.');
      if (err.response?.status === 401) handleLogout();
    } finally {
      setLoading(false);
    }
  }, [handleLogout]);

  useEffect(() => { fetchData(); }, [fetchData]);

  if (loading) return <h2>Loading dashboard...</h2>;

  return (
    <div style={{ padding: '20px' }}>
      {/* Header with logo, tenant name, owner email, and logout */}
      <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', marginBottom: '20px' }}>
        <div style={{ display: 'flex', alignItems: 'center', gap: '15px' }}>
          {tenantInfo && tenantInfo.logoUrl && (
            <img
              src={tenantInfo.logoUrl}
              alt={`${tenantInfo.name} Logo`}
              style={{ height: '50px', width: '50px', objectFit: 'contain' }}
            />
          )}
          <div>
            <h2 style={{ margin: 0 }}>{tenantInfo ? tenantInfo.name : 'Dashboard'}</h2>
            <p style={{ margin: '5px 0', color: '#555' }}>
              Owner: {tenantInfo ? tenantInfo.adminEmail : '...'}
            </p>
          </div>
        </div>
        <button onClick={handleLogout} style={{ padding: '8px 12px', width: 'auto' }}>Logout</button>
      </div>

      {/* Navigation Links */}
      <nav style={{ margin: '20px 0', display: 'flex', gap: '15px' }}>
        <Link to="/dashboard">Overview</Link>
        <Link to="/dashboard/sales-trend">Sales Trend</Link>
        <Link to="/dashboard/orders">Orders</Link>
        <Link to="/dashboard/top-customers">Top Customers</Link>
        <Link to="/dashboard/customer-history">Customer History</Link>
      </nav>

      {/* Display error if any */}
      {error && <div style={{ color: 'red', margin: '10px 0' }}>{error}</div>}

      {/* Render the subpage */}
      <Outlet context={{ overview, eventSummary, topCustomersChartData, salesTrend }} />
    </div>
  );
}
