import React, { useEffect, useState, useCallback } from 'react';
import { useNavigate } from 'react-router-dom';
import { jwtDecode } from 'jwt-decode';
import API from '../api';

// --- ADDED: Imports for the new charts ---
import SalesTrendChart from '../components/SalesTrendChart';
import TopCustomersBarChart from '../components/TopCustomersBarChart';

export default function DashboardPage() {
  const [tenantInfo, setTenantInfo] = useState(null);
  const [overview, setOverview] = useState(null);
  const [eventSummary, setEventSummary] = useState(null);
  const [topCustomers, setTopCustomers] = useState([]);
  const [orders, setOrders] = useState([]);
  const [customerHistory, setCustomerHistory] = useState([]);
  const [error, setError] = useState('');
  const [loading, setLoading] = useState(true);
  const [ingesting, setIngesting] = useState(false);
  const [startDate, setStartDate] = useState('');
  const [endDate, setEndDate] = useState('');

  // --- ADDED: New state for chart data ---
  const [salesTrend, setSalesTrend] = useState([]);
  const [topCustomersChartData, setTopCustomersChartData] = useState([]);

  const navigate = useNavigate();

  const handleLogout = useCallback(() => { /* ... (unchanged) ... */ }, [navigate]);

  // --- MODIFIED: fetchData now gets chart data as well ---
  const fetchData = useCallback(async () => {
    try {
      setLoading(true);
      const [
        tenantInfoRes, overviewRes, eventSummaryRes, topCustomersRes,
        ordersRes, customerHistoryRes, salesTrendRes, topCustomersChartRes
      ] = await Promise.all([
        API.get('/dashboard/tenant-info'),
        API.get('/dashboard/overview'),
        API.get('/dashboard/events-summary'),
        API.get('/dashboard/top-customers'),
        API.get('/dashboard/orders'),
        API.get('/dashboard/customer-products'),
        API.get('/dashboard/sales-trend'), // New API call
        API.get('/dashboard/top-customers-chart') // New API call
      ]);

      setTenantInfo(tenantInfoRes.data);
      setOverview(overviewRes.data);
      setEventSummary(eventSummaryRes.data);
      setTopCustomers(topCustomersRes.data.top);
      setOrders(ordersRes.data.orders);
      setCustomerHistory(customerHistoryRes.data);
      setSalesTrend(salesTrendRes.data);
      setTopCustomersChartData(topCustomersChartRes.data);

      setError('');
    } catch (err) {
      setError('Failed to fetch dashboard data.');
      if (err.response?.status === 401) handleLogout();
    } finally {
      setLoading(false);
    }
  }, [handleLogout]);

  useEffect(() => { /* ... (unchanged) ... */ }, [fetchData]);

  const handleIngestData = async () => { /* ... (unchanged) ... */ };
  const handleFilterOrders = async () => { /* ... (unchanged) ... */ };

  // --- ADDED: Calculation for the new KPI ---
  const averageOrderValue = (overview && overview.totalOrders > 0)
    ? (overview.revenue / overview.totalOrders)
    : 0;

  if (loading) return <h2>Loading dashboard...</h2>;

  return (
    // --- Your original layout is untouched ---
    <div style={{ padding: '20px' }}>
      <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', marginBottom: '20px' }}>
        {/* ... (your header is unchanged) ... */}
      </div>

      {overview && overview.totalCustomers === 0 && (
        <div style={{ padding: '20px', margin: '20px 0', backgroundColor: '#eef', border: '1px solid #ccf' }}>
          {/* ... (your 'Load Data' block is unchanged) ... */}
        </div>
      )}
      {error && <div style={{ color: 'red', margin: '10px 0' }}>{error}</div>}

      {/* --- MODIFIED: Added AOV to your original Overview widget --- */}
      <div className="widget">
        <h3>Overview</h3>
        {overview && (
          <div>
            <div>Total Customers: {overview.totalCustomers}</div>
            <div>Total Orders: {overview.totalOrders}</div>
            <div>Revenue: ₹{parseFloat(overview.revenue).toFixed(2)}</div>
            <div>Average Order Value: ₹{averageOrderValue.toFixed(2)}</div>
          </div>
        )}
      </div>

      {/* --- ADDED: New Sales Trend Chart Widget --- */}
      <div className="widget">
        <h3>Sales Trend (Last 30 Days)</h3>
        <SalesTrendChart data={salesTrend} />
      </div>

      <div className="widget">
        <h3>Activity</h3>
        {eventSummary && (
          <div>
            <div>Checkouts Started: {eventSummary.checkout_started || 0}</div>
            <div>Abandoned Carts: {eventSummary.cart_abandoned || 0}</div>
          </div>
        )}
      </div>

      {/* --- MODIFIED: Replaced Top Customers Table with a Bar Chart --- */}
      <div className="widget">
        <h3>Top 5 Customers by Spend</h3>
        <TopCustomersBarChart data={topCustomersChartData} />
      </div>

      {/* --- Your original tables below are untouched --- */}
      <div className="widget">
        <h3>Orders by Date</h3>
        {/* ... (your full orders table is unchanged) ... */}
      </div>

      <div className="widget">
        <h3>Customer Purchase History</h3>
        {/* ... (your full customer history table is unchanged) ... */}
      </div>
    </div>
  );
}
