import React, { useEffect, useState } from 'react';
import { useOutletContext } from 'react-router-dom';
import SalesTrendChart from '../components/SalesTrendChart';
import API from '../api';

export default function SalesTrendPage() {
  const { salesTrend: initialSalesTrend, dataSource } = useOutletContext();
  const [salesTrend, setSalesTrend] = useState(initialSalesTrend || []);
  const [loading, setLoading] = useState(false);

  useEffect(() => {
    const fetchShopifySalesTrend = async () => {
      if (dataSource !== 'shopify') {
        setSalesTrend(initialSalesTrend || []);
        return;
      }

      try {
        setLoading(true);

        // Fetch Shopify orders
        const ordersRes = await API.get('/shopify/sync/orders');
        const orders = ordersRes.data.data || [];

        // Generate last 30 days
        const today = new Date();
        const last30Days = Array.from({ length: 30 }, (_, i) => {
          const date = new Date(today);
          date.setDate(today.getDate() - i);
          return date.toISOString().slice(0, 10);
        }).reverse();

        // Aggregate daily revenue
        const trendData = last30Days.map(date => {
          const dailyTotal = orders
            .filter(order => order.created_at?.startsWith(date))
            .reduce((sum, order) => sum + parseFloat(order.total_price || 0), 0);
          return { date, revenue: dailyTotal };
        });

        setSalesTrend(trendData);
      } catch (err) {
        console.error('Failed to fetch Shopify sales trend:', err);
        setSalesTrend([]);
      } finally {
        setLoading(false);
      }
    };

    fetchShopifySalesTrend();
  }, [dataSource, initialSalesTrend]);

  if (loading) return <h3>Loading Sales Trend...</h3>;

  return (
    <div className="widget">
      <h3>Sales Trend (Last 30 Days)</h3>
      <SalesTrendChart data={salesTrend} />
    </div>
  );
}
