import React, { useEffect, useState, useCallback } from 'react';
import { useNavigate } from 'react-router-dom';
import { jwtDecode } from 'jwt-decode'; // Ensure correct import
import API from '../api';

export default function DashboardPage() {
  const [tenantInfo, setTenantInfo] = useState(null);
  const [overview, setOverview] = useState(null);
  const [error, setError] = useState('');
  const [loading, setLoading] = useState(true);
  const [ingesting, setIngesting] = useState(false);
  const navigate = useNavigate();

  const handleLogout = useCallback(() => {
    localStorage.removeItem('token');
    navigate('/login');
  }, [navigate]);

  const fetchOverview = useCallback(async () => {
    try {
      setLoading(true);
      const [tenantRes, overviewRes] = await Promise.all([
        API.get('/dashboard/tenant-info'),
        API.get('/dashboard/overview')
      ]);
      setTenantInfo(tenantRes.data);
      setOverview(overviewRes.data);
      setError('');
    } catch (err) {
      setError('Failed to fetch dashboard data.');
      if (err.response?.status === 401) handleLogout();
    } finally {
      setLoading(false);
    }
  }, [handleLogout]);

  useEffect(() => { fetchOverview(); }, [fetchOverview]);

  const handleIngestData = async () => {
    try {
      setIngesting(true);
      setError('');
      const token = localStorage.getItem('token');
      const { tenantId } = jwtDecode(token);

      await API.post(`/ingest/tenant/${tenantId}/products`, [/* sample products */]);
      await API.post(`/ingest/tenant/${tenantId}/customers`, [/* sample customers */]);
      await API.post(`/ingest/tenant/${tenantId}/orders`, [/* sample orders */]);
      await API.post(`/ingest/tenant/${tenantId}/events`, [/* sample events */]);

      await fetchOverview();
    } catch (err) {
      setError('Failed to ingest data. Please check the server logs.');
    } finally {
      setIngesting(false);
    }
  };

  if (loading) return <h2>Loading dashboard...</h2>;

  return (
    <div style={{ padding: '20px' }}>
      <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', marginBottom: '20px' }}>
        <div>
          <h2>{tenantInfo ? tenantInfo.name : 'Dashboard'}</h2>
          <p>Owner: {tenantInfo ? tenantInfo.adminEmail : '...'}</p>
        </div>
        <button onClick={handleLogout}>Logout</button>
      </div>

      {/* Load Sample Data */}
      <div style={{ padding: '20px', margin: '20px 0', backgroundColor: '#eef', border: '1px solid #ccf' }}>
        <h4>Load Sample Data</h4>
        <p>Click the button below to load sample customer and order data.</p>
        <button onClick={handleIngestData} disabled={ingesting}>
          {ingesting ? 'Ingesting...' : 'Load Sample Data'}
        </button>
      </div>

      {/* Overview */}
      <div className="widget">
        <h3>Overview</h3>
        {overview && (
          <div>
            <div>Total Customers: {overview.totalCustomers}</div>
            <div>Total Orders: {overview.totalOrders}</div>
            <div>Revenue: â‚¹{parseFloat(overview.revenue).toFixed(2)}</div>
          </div>
        )}
      </div>

      {error && <div style={{ color: 'red', margin: '10px 0' }}>{error}</div>}
    </div>
  );
}
