import React, { useEffect, useState, useCallback } from 'react';
import { useNavigate } from 'react-router-dom';
import { jwtDecode } from 'jwt-decode';
import API from '../api';

export default function DashboardPage() {
  const [tenantInfo, setTenantInfo] = useState(null);
  const [overview, setOverview] = useState(null);
  const [eventSummary, setEventSummary] = useState(null);
  const [topCustomers, setTopCustomers] = useState([]);
  const [orders, setOrders] = useState([]);
  const [customerHistory, setCustomerHistory] = useState([]);
  const [error, setError] = useState('');
  const [loading, setLoading] = useState(true);
  const [ingesting, setIngesting] = useState(false);
  const [startDate, setStartDate] = useState('');
  const [endDate, setEndDate] = useState('');

  const navigate = useNavigate();

  const handleLogout = useCallback(() => {
    localStorage.removeItem('token');
    navigate('/login');
  }, [navigate]);

  const fetchData = useCallback(async () => {
    try {
      setLoading(true);
      const tenantInfoRes = await API.get('/dashboard/tenant-info');
      const overviewRes = await API.get('/dashboard/overview');
      const eventSummaryRes = await API.get('/dashboard/events-summary');
      const topCustomersRes = await API.get('/dashboard/top-customers');
      const ordersRes = await API.get('/dashboard/orders');
      const customerHistoryRes = await API.get('/dashboard/customer-products');

      setTenantInfo(tenantInfoRes.data);
      setOverview(overviewRes.data);
      setEventSummary(eventSummaryRes.data);
      setTopCustomers(topCustomersRes.data.top);
      setOrders(ordersRes.data.orders);
      setCustomerHistory(customerHistoryRes.data);
      setError('');
    } catch (err) {
      setError('Failed to fetch dashboard data.');
      if (err.response?.status === 401) handleLogout();
    } finally {
      setLoading(false);
    }
  }, [handleLogout]);

  useEffect(() => {
    fetchData();
  }, [fetchData]);

  // --- MODIFIED: Added 10+ sample data points ---
  const handleIngestData = async () => {
    try {
      setIngesting(true);
      setError('');
      const token = localStorage.getItem('token');
      const { tenantId } = jwtDecode(token);

      // 1. Ingest Products
      await API.post(`/ingest/tenant/${tenantId}/products`, [
        { "id": 101, "title": "Classic T-Shirt", "price": "25.00" },
        { "id": 102, "title": "Denim Jeans", "price": "75.00" },
        { "id": 103, "title": "Leather Belt", "price": "35.00" },
        { "id": 104, "title": "Sunglasses", "price": "55.00" },
        { "id": 105, "title": "Cozy Hoodie", "price": "60.00" },
        { "id": 106, "title": "Knit Beanie", "price": "20.00" },
      ]);

      // 2. Ingest Customers
      await API.post(`/ingest/tenant/${tenantId}/customers`, [
        { "id": 201, "first_name": "Riya", "last_name": "Singh", "email": "riya.s@example.com" },
        { "id": 202, "first_name": "Ayush", "last_name": "Raj", "email": "ayush.r@example.com" },
        { "id": 203, "first_name": "Dev", "last_name": "Raj", "email": "dev.r@example.com" },
        { "id": 204, "first_name": "Kriti", "last_name": "Saini", "email": "kriti.s@example.com" },
        { "id": 205, "first_name": "Trisha", "last_name": "Singh", "email": "trisha.s@example.com" },
        { "id": 206, "first_name": "Rani", "last_name": "Kumari", "email": "rani.k@example.com" },
        { "id": 207, "first_name": "Hitesh", "last_name": "Raj", "email": "hitesh.r@example.com" },
        { "id": 208, "first_name": "Suraj", "last_name": "Prakash", "email": "suraj.p@example.com" },
        { "id": 209, "first_name": "Ronny", "last_name": "Raj", "email": "ronny.r@example.com" },
        { "id": 210, "first_name": "Anant", "last_name": "Singh", "email": "anant.smith@example.com" },
      ]);

      // 3. Ingest Orders
      await API.post(`/ingest/tenant/${tenantId}/orders`, [
        { "id": 1001, "total_price": "100.00", "created_at": "2025-09-10T10:00:00Z", "customer": { "id": 201 }, "line_items": [{ "product_id": 101, "quantity": 1, "price": "25.00" }, { "product_id": 102, "quantity": 1, "price": "75.00" }] },
        { "id": 1002, "total_price": "50.00", "created_at": "2025-09-09T15:30:00Z", "customer": { "id": 202 }, "line_items": [{ "product_id": 101, "quantity": 2, "price": "25.00" }] },
        { "id": 1003, "total_price": "90.00", "created_at": "2025-09-08T11:00:00Z", "customer": { "id": 203 }, "line_items": [{ "product_id": 103, "quantity": 1, "price": "35.00" }, { "product_id": 104, "quantity": 1, "price": "55.00" }] },
        { "id": 1004, "total_price": "150.00", "created_at": "2025-09-07T18:45:00Z", "customer": { "id": 202 }, "line_items": [{ "product_id": 102, "quantity": 2, "price": "75.00" }] },
        { "id": 1005, "total_price": "80.00", "created_at": "2025-09-06T12:00:00Z", "customer": { "id": 204 }, "line_items": [{ "product_id": 105, "quantity": 1, "price": "60.00" }, { "product_id": 106, "quantity": 1, "price": "20.00" }] },
        { "id": 1006, "total_price": "25.00", "created_at": "2025-09-05T09:20:00Z", "customer": { "id": 205 }, "line_items": [{ "product_id": 101, "quantity": 1, "price": "25.00" }] },
        { "id": 1007, "total_price": "135.00", "created_at": "2025-09-04T14:55:00Z", "customer": { "id": 206 }, "line_items": [{ "product_id": 102, "quantity": 1, "price": "75.00" }, { "product_id": 105, "quantity": 1, "price": "60.00" }] },
        { "id": 1008, "total_price": "75.00", "created_at": "2025-09-03T20:10:00Z", "customer": { "id": 207 }, "line_items": [{ "product_id": 102, "quantity": 1, "price": "75.00" }] },
        { "id": 1009, "total_price": "115.00", "created_at": "2025-09-02T13:00:00Z", "customer": { "id": 201 }, "line_items": [{ "product_id": 104, "quantity": 1, "price": "55.00" }, { "product_id": 105, "quantity": 1, "price": "60.00" }] },
        { "id": 1010, "total_price": "40.00", "created_at": "2025-09-01T16:00:00Z", "customer": { "id": 208 }, "line_items": [{ "product_id": 106, "quantity": 2, "price": "20.00" }] },
        { "id": 1011, "total_price": "60.00", "created_at": "2025-08-31T10:00:00Z", "customer": { "id": 209 }, "line_items": [{ "product_id": 105, "quantity": 1, "price": "60.00" }] },
        { "id": 1012, "total_price": "55.00", "created_at": "2025-08-30T11:40:00Z", "customer": { "id": 210 }, "line_items": [{ "product_id": 104, "quantity": 1, "price": "55.00" }] },
        { "id": 1013, "total_price": "95.00", "created_at": "2025-08-29T17:00:00Z", "customer": { "id": 203 }, "line_items": [{ "product_id": 102, "quantity": 1, "price": "75.00" }, { "product_id": 106, "quantity": 1, "price": "20.00" }] },
        { "id": 1014, "total_price": "120.00", "created_at": "2025-08-28T19:00:00Z", "customer": { "id": 202 }, "line_items": [{ "product_id": 105, "quantity": 2, "price": "60.00" }] },
        { "id": 1015, "total_price": "35.00", "created_at": "2025-08-27T08:00:00Z", "customer": { "id": 201 }, "line_items": [{ "product_id": 103, "quantity": 1, "price": "35.00" }] },
      ]);

      // 4. Ingest Sample Events
      await API.post(`/ingest/tenant/${tenantId}/events`, [
        { "eventName": "checkout_started", "customerId": 201 },
        { "eventName": "checkout_started", "customerId": 202 },
        { "eventName": "checkout_started", "customerId": 203 },
        { "eventName": "checkout_started", "customerId": 204 },
        { "eventName": "checkout_started", "customerId": 205 },
        { "eventName": "checkout_started", "customerId": 206 },
        { "eventName": "checkout_started", "customerId": 207 },
        { "eventName": "cart_abandoned", "customerId": 208 },
        { "eventName": "cart_abandoned", "customerId": 209 },
        { "eventName": "cart_abandoned", "customerId": 210 },
      ]);

      await fetchData();
    } catch (err) {
      setError('Failed to ingest data. Please check the server logs.');
    } finally {
      setIngesting(false);
    }
  };

  const handleFilterOrders = async () => {
    try {
      setError('');
      const params = new URLSearchParams();
      if (startDate) params.append('from', startDate);
      if (endDate) params.append('to', endDate);

      const res = await API.get(`/dashboard/orders?${params.toString()}`);
      setOrders(res.data.orders);
    } catch (err) {
      setError('Failed to filter orders.');
    }
  };

  if (loading) return <h2>Loading dashboard...</h2>;

  return (
    <div style={{ padding: '20px' }}>
      <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', marginBottom: '20px' }}>
        <div style={{ display: 'flex', alignItems: 'center', gap: '15px' }}>
          {tenantInfo && tenantInfo.logoUrl && (
            <img 
              src={tenantInfo.logoUrl} 
              alt={`${tenantInfo.name} Logo`} 
              style={{ height: '50px', width: '50px', objectFit: 'contain' }} 
            />
          )}
          <div>
            <h2 style={{ margin: 0 }}>
              {tenantInfo ? tenantInfo.name : 'Dashboard'}
            </h2>
            <p style={{ margin: '5px 0', color: '#555' }}>
              Owner: {tenantInfo ? tenantInfo.adminEmail : '...'}
            </p>
          </div>
        </div>
        <button onClick={handleLogout} style={{ padding: '8px 12px', width: 'auto' }}>
            Logout
        </button>
      </div>

      {overview && overview.totalCustomers === 0 && (
        <div style={{ padding: '20px', margin: '20px 0', backgroundColor: '#eef', border: '1px solid #ccf' }}>
          <h4>Your dashboard is empty!</h4>
          <p>Click the button below to load some sample customer and order data.</p>
          <button onClick={handleIngestData} disabled={ingesting} style={{ width: 'auto' }}>
            {ingesting ? 'Ingesting...' : 'Load Sample Data'}
          </button>
        </div>
      )}
      {error && <div style={{ color: 'red', margin: '10px 0' }}>{error}</div>}

      <div className="widget">
        <h3>Overview</h3>
        {overview && (
          <div>
            <div>Total Customers: {overview.totalCustomers}</div>
            <div>Total Orders: {overview.totalOrders}</div>
            <div>Revenue: ${parseFloat(overview.revenue).toFixed(2)}</div>
          </div>
        )}
      </div>

      <div className="widget">
        <h3>Activity</h3>
        {eventSummary && (
        <div>
            <div>Checkouts Started: {eventSummary.checkout_started || 0}</div>
            <div>Abandoned Carts: {eventSummary.cart_abandoned || 0}</div>
        </div>
        )}
      </div>

      <div className="widget">
        <h3>Top 5 Customers by Spend</h3>
        <table border="1" cellPadding="8" style={{ borderCollapse: 'collapse', width: '100%' }}>
          <thead>
            <tr>
              <th>Name</th>
              <th>Email</th>
              <th>Total Spent</th>
            </tr>
          </thead>
          <tbody>
            {topCustomers.map((customer) => (
              <tr key={customer.id}>
                <td>{customer.firstName} {customer.lastName}</td>
                <td>{customer.email}</td>
                <td>${parseFloat(customer.totalSpent).toFixed(2)}</td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>

      <div className="widget">
        <h3>Orders by Date</h3>
        <div style={{ display: 'flex', alignItems: 'center', gap: '10px', marginBottom: '20px' }}>
          <label>From:</label>
          <input type="date" value={startDate} onChange={e => setStartDate(e.target.value)} />
          <label>To:</label>
          <input type="date" value={endDate} onChange={e => setEndDate(e.target.value)} />
          <button onClick={handleFilterOrders} style={{ width: 'auto' }}>Filter</button>
        </div>

        <table border="1" cellPadding="8" style={{ borderCollapse: 'collapse', width: '100%', marginTop: '20px' }}>
          <thead>
              <tr>
                  <th>Order ID</th>
                  <th>Products</th>
                  <th>Total Price</th>
                  <th>Date</th>
              </tr>
          </thead>
          <tbody>
              {orders.map((order) => (
                  <tr key={order.id}>
                      <td>{order.shopifyId}</td>
                      <td>
                        {order.Products && order.Products.length > 0
                          ? order.Products.map(p => p.title).join(', ')
                          : 'N/A'}
                      </td>
                      <td>${parseFloat(order.totalPrice).toFixed(2)}</td>
                      <td>{new Date(order.createdAtShopify).toLocaleDateString()}</td>
                  </tr>
              ))}
          </tbody>
        </table>
      </div>

      <div className="widget">
        <h3>Customer Purchase History</h3>
        <table border="1" cellPadding="8" style={{ borderCollapse: 'collapse', width: '100%', marginTop: '20px' }}>
          <thead>
            <tr>
              <th>Customer</th>
              <th>Email</th>
              <th>Order IDs</th>
            </tr>
          </thead>
          <tbody>
            {customerHistory.map((customer) => (
              <tr key={customer.id}>
                <td>{customer.name}</td>
                <td>{customer.email}</td>
                <td>
                  {customer.orders.length > 0
                    ? <ul>{customer.orders.map(orderId => <li key={orderId}>{orderId}</li>)}</ul>
                    : 'No orders found'}
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
    </div>
  );
}
